import{_ as r,C as e,c as l,o,j as a,G as i,aQ as n,a as h}from"./chunks/framework.DEi4YP2n.js";const s="/assets/social-1.CmuLsPxL.png",_=JSON.parse('{"title":"我国“五险一金”体系","description":"","frontmatter":{},"headers":[],"relativePath":"column/CommonSense/生活常识/我国的社保体系.md","filePath":"column/CommonSense/生活常识/我国的社保体系.md","lastUpdated":1762823005000}'),c={name:"column/CommonSense/生活常识/我国的社保体系.md"};function b(u,t,m,p,g,q){const d=e("ArticleMetadata");return o(),l("div",null,[t[0]||(t[0]=a("h1",{id:"我国-五险一金-体系",tabindex:"-1"},[h("我国“五险一金”体系 "),a("a",{class:"header-anchor",href:"#我国-五险一金-体系","aria-label":'Permalink to "我国“五险一金”体系"'},"​")],-1)),i(d),t[1]||(t[1]=n('<nav class="table-of-contents"><ul><li><a href="#🧭-一、什么是-五险一金">🧭 一、什么是“五险一金”？</a></li><li><a href="#🧩-二、-五险一金-缴纳组成一览表">🧩 二、“五险一金”缴纳组成一览表</a></li><li><a href="#🧾-三、住房公积金详解-一金">🧾 三、住房公积金详解（“一金”）</a><ul><li><a href="#✅-什么是住房公积金">✅ 什么是住房公积金？</a></li><li><a href="#🏠-公积金的主要用途">🏠 公积金的主要用途</a></li><li><a href="#💼-公积金举例说明-北京为例">💼 公积金举例说明（北京为例）</a></li></ul></li><li><a href="#🔁-四、-五险一金-与工资的关系">🔁 四、“五险一金”与工资的关系</a><ul><li><a href="#📉-到手工资怎么计算">📉 到手工资怎么计算？</a></li></ul></li><li><a href="#📚-五、-五险一金-常见用途对照表">📚 五、“五险一金”常见用途对照表</a></li><li><a href="#❓-六、常见问题-q-amp-a">❓ 六、常见问题 Q&amp;amp;A</a><ul><li><a href="#q1-单位不给交社保和公积金合法吗">Q1：单位不给交社保和公积金合法吗？</a></li><li><a href="#q2-自己能交-五险一金-吗">Q2：自己能交“五险一金”吗？</a></li><li><a href="#q3-换工作了-五险一金怎么办">Q3：换工作了，五险一金怎么办？</a></li></ul></li></ul></nav><h2 id="🧭-一、什么是-五险一金" tabindex="-1">🧭 一、什么是“五险一金”？ <a class="header-anchor" href="#🧭-一、什么是-五险一金" aria-label="Permalink to &quot;🧭 一、什么是“五险一金”？&quot;">​</a></h2><p>“五险一金”是中国用人单位依法必须为职工缴纳的<strong>社会保障+住房福利制度</strong>，全称包括：</p><table tabindex="0"><thead><tr><th>类别</th><th>内容</th></tr></thead><tbody><tr><td>🖐 <strong>五险</strong></td><td>养老保险、医疗保险、失业保险、工伤保险、生育保险</td></tr><tr><td>🏠 <strong>一金</strong></td><td>住房公积金</td></tr></tbody></table><p>✅ <strong>法律强制性缴纳</strong>：</p><ul><li>用人单位必须为在职员工缴纳</li><li>属于员工法定权益</li><li>一般在员工工资中体现为“代扣代缴”</li></ul><p><img src="'+s+'" alt="" loading="lazy"></p><h2 id="🧩-二、-五险一金-缴纳组成一览表" tabindex="-1">🧩 二、“五险一金”缴纳组成一览表 <a class="header-anchor" href="#🧩-二、-五险一金-缴纳组成一览表" aria-label="Permalink to &quot;🧩 二、“五险一金”缴纳组成一览表&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>项目</strong></th><th><strong>缴费主体</strong></th><th><strong>单位缴费比例（约）</strong></th><th><strong>个人缴费比例（约）</strong></th><th><strong>主要作用</strong></th></tr></thead><tbody><tr><td>🧓 养老保险</td><td>单位 + 个人</td><td>16%</td><td>8%</td><td>退休后领取养老金</td></tr><tr><td>🏥 医疗保险</td><td>单位 + 个人</td><td>10%</td><td>2% + 3 元</td><td>医疗报销、看病用医保卡</td></tr><tr><td>🏚 失业保险</td><td>单位 + 个人</td><td>0.5% ~ 1%</td><td>0.2%</td><td>失业时可领取失业金</td></tr><tr><td>💼 工伤保险</td><td>单位（个人不缴）</td><td>0.2% ~ 1.9%</td><td>0</td><td>因工受伤、住院赔偿</td></tr><tr><td>🤰 生育保险</td><td>单位（个人不缴）</td><td>0.5% ~ 1%</td><td>0</td><td>怀孕生产津贴、产检住院报销</td></tr><tr><td>🏠 公积金</td><td>单位 + 个人</td><td>5% ~ 12%（各地浮动）</td><td>5% ~ 12%（对等缴纳）</td><td>购房首付、公积金贷款、租房提取等</td></tr></tbody></table><blockquote><p>⚠️ 各地区比例略有不同，以上为普遍区间，请以当地人社局/住房公积金中心为准。</p></blockquote><h2 id="🧾-三、住房公积金详解-一金" tabindex="-1">🧾 三、住房公积金详解（“一金”） <a class="header-anchor" href="#🧾-三、住房公积金详解-一金" aria-label="Permalink to &quot;🧾 三、住房公积金详解（“一金”）&quot;">​</a></h2><h3 id="✅-什么是住房公积金" tabindex="-1">✅ 什么是住房公积金？ <a class="header-anchor" href="#✅-什么是住房公积金" aria-label="Permalink to &quot;✅ 什么是住房公积金？&quot;">​</a></h3><ul><li>是国家为帮助职工解决住房问题而设立的福利制度；</li><li><strong>单位和个人按月等额缴纳</strong>；</li><li>储存在个人专属“公积金账户”中；</li><li><strong>全部归个人所有</strong>，可提取、可贷款。</li></ul><h3 id="🏠-公积金的主要用途" tabindex="-1">🏠 公积金的主要用途 <a class="header-anchor" href="#🏠-公积金的主要用途" aria-label="Permalink to &quot;🏠 公积金的主要用途&quot;">​</a></h3><table tabindex="0"><thead><tr><th>用途</th><th>说明</th></tr></thead><tbody><tr><td>💰 买房首付</td><td>可提取公积金用于购房（首套为主）</td></tr><tr><td>🏦 公积金贷款</td><td>可申请低利率的公积金贷款，利率远低于商贷</td></tr><tr><td>🏘️ 租房提取</td><td>无购房也可按月或年度提取用于租房</td></tr><tr><td>🧾 重大支出提取</td><td>疾病、失业、退休、出境定居等情况可支取</td></tr><tr><td>👥 转移合并</td><td>跳槽后可转到新单位，账户合并</td></tr><tr><td>🧓 退休提取</td><td>退休时可一次性全部支取</td></tr></tbody></table><h3 id="💼-公积金举例说明-北京为例" tabindex="-1">💼 公积金举例说明（北京为例） <a class="header-anchor" href="#💼-公积金举例说明-北京为例" aria-label="Permalink to &quot;💼 公积金举例说明（北京为例）&quot;">​</a></h3><ul><li>假设职工月工资 10,000 元，缴纳比例 12%</li><li>单位缴纳 1,200 元，个人缴纳 1,200 元</li><li>每月入账公积金账户：2,400 元</li></ul><p>✅ 一年后账户余额约 2.9 万，买房时可全额提取或用于贷款。</p><h2 id="🔁-四、-五险一金-与工资的关系" tabindex="-1">🔁 四、“五险一金”与工资的关系 <a class="header-anchor" href="#🔁-四、-五险一金-与工资的关系" aria-label="Permalink to &quot;🔁 四、“五险一金”与工资的关系&quot;">​</a></h2><h3 id="📉-到手工资怎么计算" tabindex="-1">📉 到手工资怎么计算？ <a class="header-anchor" href="#📉-到手工资怎么计算" aria-label="Permalink to &quot;📉 到手工资怎么计算？&quot;">​</a></h3><p>假设月工资为 <strong>10,000 元</strong>，缴费如下（按高比例地区估算）：</p><table tabindex="0"><thead><tr><th>项目</th><th>扣款金额（约）</th></tr></thead><tbody><tr><td>养老（8%）</td><td>800 元</td></tr><tr><td>医疗（2%+3 元）</td><td>203 元</td></tr><tr><td>失业（0.2%）</td><td>20 元</td></tr><tr><td>公积金（12%）</td><td>1,200 元</td></tr><tr><td><strong>合计</strong></td><td><strong>2,223 元</strong></td></tr></tbody></table><p>👉 <strong>到手工资 ≈ 7,777 元</strong>（不含个税，仅供参考）</p><h2 id="📚-五、-五险一金-常见用途对照表" tabindex="-1">📚 五、“五险一金”常见用途对照表 <a class="header-anchor" href="#📚-五、-五险一金-常见用途对照表" aria-label="Permalink to &quot;📚 五、“五险一金”常见用途对照表&quot;">​</a></h2><table tabindex="0"><thead><tr><th>项目</th><th>是否强制</th><th>用于生活中的什么？</th></tr></thead><tbody><tr><td>养老保险</td><td>✅ 是</td><td>退休后每月领取养老金</td></tr><tr><td>医疗保险</td><td>✅ 是</td><td>看病报销、医保卡支付门诊费</td></tr><tr><td>失业保险</td><td>✅ 是</td><td>被裁员时可领取失业金</td></tr><tr><td>工伤保险</td><td>✅ 是</td><td>工伤住院、误工补助、一次性伤残金</td></tr><tr><td>生育保险</td><td>✅ 是</td><td>女职工生育津贴、产检、住院费用报销</td></tr><tr><td>公积金</td><td>✅ 是</td><td>买房首付、住房贷款、租房提取、重大困难支取等</td></tr></tbody></table><h2 id="❓-六、常见问题-q-amp-a" tabindex="-1">❓ 六、常见问题 Q&amp;A <a class="header-anchor" href="#❓-六、常见问题-q-amp-a" aria-label="Permalink to &quot;❓ 六、常见问题 Q\\&amp;A&quot;">​</a></h2><h3 id="q1-单位不给交社保和公积金合法吗" tabindex="-1">Q1：单位不给交社保和公积金合法吗？ <a class="header-anchor" href="#q1-单位不给交社保和公积金合法吗" aria-label="Permalink to &quot;Q1：单位不给交社保和公积金合法吗？&quot;">​</a></h3><p>❌ 不合法。法律规定，<strong>用人单位必须为职工缴纳五险一金</strong>。如不交，员工可向人社局举报。</p><h3 id="q2-自己能交-五险一金-吗" tabindex="-1">Q2：自己能交“五险一金”吗？ <a class="header-anchor" href="#q2-自己能交-五险一金-吗" aria-label="Permalink to &quot;Q2：自己能交“五险一金”吗？&quot;">​</a></h3><ul><li><strong>五险</strong>：自由职业者可通过灵活就业方式缴纳“养老+医疗”，其余险种暂不可单独缴；</li><li><strong>一金（公积金）</strong>：部分城市开放“自由缴存公积金”，但覆盖有限。</li></ul><h3 id="q3-换工作了-五险一金怎么办" tabindex="-1">Q3：换工作了，五险一金怎么办？ <a class="header-anchor" href="#q3-换工作了-五险一金怎么办" aria-label="Permalink to &quot;Q3：换工作了，五险一金怎么办？&quot;">​</a></h3><ul><li>可直接由新单位继续缴纳，无需中断；</li><li>可通过**“社保转移”+“公积金转移”**合并账户。</li></ul><div class="tip custom-block"><p class="custom-block-title">总结</p><p>“五险一金”是每个职工最基础也是最重要的保障体系，影响你的一生：从<strong>看病、生娃、失业、买房、退休</strong>，每一个关键节点都与你缴纳的五险一金息息相关。</p></div>',33))])}const x=r(c,[["render",b]]);export{_ as __pageData,x as default};
