import{_ as a,C as e,c as l,o as n,j as t,G as s,aQ as i,a as g}from"./chunks/framework.DEi4YP2n.js";const d="/assets/CDN-1.kU7Xuexj.png",b=JSON.parse('{"title":"CDN 内容分发网络","description":"","frontmatter":{},"headers":[],"relativePath":"column/Network/Web传输/CDN内容分发.md","filePath":"column/Network/Web传输/CDN内容分发.md","lastUpdated":1757667778000}'),c={name:"column/Network/Web传输/CDN内容分发.md"};function h(u,o,_,p,D,C){const r=e("ArticleMetadata");return n(),l("div",null,[o[0]||(o[0]=t("h1",{id:"cdn-内容分发网络",tabindex:"-1"},[t("strong",null,"CDN 内容分发网络"),g(),t("a",{class:"header-anchor",href:"#cdn-内容分发网络","aria-label":'Permalink to "**CDN 内容分发网络**"'},"​")],-1)),s(r),o[1]||(o[1]=i('<h2 id="_1-什么是-cdn" tabindex="-1"><strong>1. 什么是 CDN？</strong> <a class="header-anchor" href="#_1-什么是-cdn" aria-label="Permalink to &quot;**1. 什么是 CDN？**&quot;">​</a></h2><p><code>CDN </code>的全称是 <code>Content Delivery Network</code>，即内容分发网络。由于 <code>CDN</code> 是为加快网络访问速度而被优化的网络覆盖层，因此被形象地称为“网络加速器”。使用户可就近取得所需内容，解决 <code>Internet</code> 网络拥挤的状况，提高用户访问网站的响应速度。</p><p>👉 <strong>互联网的&quot;快递分仓&quot;</strong>：把你的网站内容（图片/视频等）提前放到离用户最近的服务器，访问时不用跨城调货，就近取件。</p><p><strong>优势</strong>：</p><p><code>CDN</code> 节点解决了跨运营商和跨地域访问的问题，访问延时大大降低；大部分请求在<code> CDN</code> 边缘节点完成，<code>CDN</code> 起到了分流作用，减轻了源站的负载。</p><h2 id="_2-核心原理" tabindex="-1"><strong>2. 核心原理</strong> <a class="header-anchor" href="#_2-核心原理" aria-label="Permalink to &quot;**2. 核心原理**&quot;">​</a></h2><p>🔄 <strong>三步走</strong>：</p><ol><li><strong>缓存</strong>：把你的网站内容复制到全球节点</li><li><strong>就近配送</strong>：用户访问时自动分配最近的节点</li><li><strong>智能回源</strong>：节点没有的内容才回原始服务器取</li></ol><h2 id="_3-四大核心作用" tabindex="-1"><strong>3. 四大核心作用</strong> <a class="header-anchor" href="#_3-四大核心作用" aria-label="Permalink to &quot;**3. 四大核心作用**&quot;">​</a></h2><p>⚡ <strong>加速</strong>：北京用户访问深圳网站，直接从北京节点取数据<br> 🛡️ <strong>抗攻击</strong>：流量分散，轻松防御 DDoS<br> 📉 <strong>减负</strong>：源服务器流量减少 90%<br> 💯 <strong>高可用</strong>：某个节点挂了立刻切换其他节点</p><h2 id="_4-工作流程" tabindex="-1"><strong>4. 工作流程</strong> <a class="header-anchor" href="#_4-工作流程" aria-label="Permalink to &quot;**4. 工作流程**&quot;">​</a></h2><p><img src="'+d+'" alt="CDN 工作流程" loading="lazy"></p><h2 id="_5-应用场景" tabindex="-1"><strong>5. 应用场景</strong> <a class="header-anchor" href="#_5-应用场景" aria-label="Permalink to &quot;**5. 应用场景**&quot;">​</a></h2><p><code>CDN</code>（内容分发网络）的应用场景非常广泛，主要围绕<strong>加速内容传输、降低延迟、减轻源站负载、提升用户体验和安全性</strong>展开。以下是典型应用场景及具体说明：</p><hr><h3 id="_1-静态资源加速" tabindex="-1"><strong>1. 静态资源加速</strong> <a class="header-anchor" href="#_1-静态资源加速" aria-label="Permalink to &quot;**1. 静态资源加速**&quot;">​</a></h3><ul><li><strong>场景</strong>：网站或应用中的静态内容（如图片、CSS、JavaScript、字体文件等）。</li><li><strong>优势</strong>： <ul><li>通过边缘节点缓存静态资源，大幅减少用户加载时间。</li><li>减轻源站带宽压力，降低服务器成本。</li></ul></li><li><strong>适用对象</strong>：电商网站、新闻门户、博客等。</li></ul><hr><h3 id="_2-视频与流媒体分发" tabindex="-1"><strong>2. 视频与流媒体分发</strong> <a class="header-anchor" href="#_2-视频与流媒体分发" aria-label="Permalink to &quot;**2. 视频与流媒体分发**&quot;">​</a></h3><ul><li><strong>场景</strong>：视频点播（VOD）、直播流媒体（如体育赛事、在线教育）。</li><li><strong>优势</strong>： <ul><li>通过全球节点就近分发视频内容，避免卡顿和缓冲。</li><li>支持自适应码率（如 HLS、DASH），适应不同网络环境。</li></ul></li><li><strong>适用对象</strong>：优酷、Netflix、抖音等视频平台。</li></ul><hr><h3 id="_3-大文件下载" tabindex="-1"><strong>3. 大文件下载</strong> <a class="header-anchor" href="#_3-大文件下载" aria-label="Permalink to &quot;**3. 大文件下载**&quot;">​</a></h3><ul><li><strong>场景</strong>：游戏安装包、软件更新、APP 分发、大型文档（如 ISO 文件）。</li><li><strong>优势</strong>： <ul><li>利用 CDN 的高带宽和并行下载能力，提升下载速度。</li><li>避免源站因大流量下载而过载。</li></ul></li><li><strong>适用对象</strong>：Steam、苹果 App Store、开源镜像站。</li></ul><hr><h3 id="_4-动态内容加速" tabindex="-1"><strong>4. 动态内容加速</strong> <a class="header-anchor" href="#_4-动态内容加速" aria-label="Permalink to &quot;**4. 动态内容加速**&quot;">​</a></h3><ul><li><strong>场景</strong>：API 接口、实时数据库查询、个性化页面（如用户登录后的动态数据）。</li><li><strong>优势</strong>： <ul><li>通过智能路由优化（如 BGP Anycast）、TCP 优化、边缘计算（如边缘脚本）降低动态内容延迟。</li><li>结合 DSA（动态站点加速）技术优化传输路径。</li></ul></li><li><strong>适用对象</strong>：社交平台（微信、微博）、在线交易系统。</li></ul><hr><h3 id="_5-安全防护-ddos-cc-防御" tabindex="-1"><strong>5. 安全防护（DDoS/CC 防御）</strong> <a class="header-anchor" href="#_5-安全防护-ddos-cc-防御" aria-label="Permalink to &quot;**5. 安全防护（DDoS/CC 防御）**&quot;">​</a></h3><ul><li><strong>场景</strong>：抵御分布式拒绝服务攻击（DDoS）、恶意爬虫、CC 攻击。</li><li><strong>优势</strong>： <ul><li>CDN 节点分散攻击流量，隐藏源站 IP。</li><li>集成 WAF（Web 应用防火墙）过滤恶意请求。</li></ul></li><li><strong>适用对象</strong>：金融、政务、游戏等高安全需求行业。</li></ul><hr>',30))])}const m=a(c,[["render",h]]);export{b as __pageData,m as default};
