import{_ as r,c as o,o as a,aP as e}from"./chunks/framework.Dxm0NwP8.js";const g=JSON.parse('{"title":"TCP/IP协议：互联网的基石","description":"","frontmatter":{},"headers":[],"relativePath":"column/Network/网络基础/TCP-IP协议.md","filePath":"column/Network/网络基础/TCP-IP协议.md"}'),d={name:"column/Network/网络基础/TCP-IP协议.md"};function l(i,t,n,s,h,P){return a(),o("div",null,t[0]||(t[0]=[e('<h1 id="tcp-ip协议-互联网的基石" tabindex="-1"><strong>TCP/IP协议：互联网的基石</strong> <a class="header-anchor" href="#tcp-ip协议-互联网的基石" aria-label="Permalink to &quot;**TCP/IP协议：互联网的基石**&quot;">​</a></h1><h2 id="_1-tcp-ip概述" tabindex="-1"><strong>1. TCP/IP概述</strong> <a class="header-anchor" href="#_1-tcp-ip概述" aria-label="Permalink to &quot;**1. TCP/IP概述**&quot;">​</a></h2><p><code>TCP/IP</code>是互联网实际使用的协议族（而<code>OSI</code>是理论模型），由<strong>4层</strong>构成：</p><table tabindex="0"><thead><tr><th>TCP/IP四层</th><th>对应OSI层</th><th>核心功能</th></tr></thead><tbody><tr><td>应用层</td><td>应用层/表示层/会话层</td><td>提供用户服务（HTTP/FTP/DNS）</td></tr><tr><td>传输层</td><td>传输层</td><td>端到端通信（TCP/UDP）</td></tr><tr><td>网络层</td><td>网络层</td><td>寻址和路由（IP/ICMP）</td></tr><tr><td>网络接口层</td><td>数据链路层/物理层</td><td>物理传输（以太网/Wi-Fi）</td></tr></tbody></table><blockquote><p>💡 <strong>关键区别</strong>：<code>TCP/IP</code>更简洁，将会话/表示层合并到应用层，数据链路/物理层合并为网络接口层。</p></blockquote><p><strong>TCP/IP 是实际实现，OSI 是理论模型</strong></p><h2 id="_2-核心协议详解" tabindex="-1"><strong>2. 核心协议详解</strong> <a class="header-anchor" href="#_2-核心协议详解" aria-label="Permalink to &quot;**2. 核心协议详解**&quot;">​</a></h2><h3 id="_1-网络接口层" tabindex="-1"><strong>（1）网络接口层</strong> <a class="header-anchor" href="#_1-网络接口层" aria-label="Permalink to &quot;**（1）网络接口层**&quot;">​</a></h3><ul><li><strong>功能</strong>：负责物理传输和本地网络寻址</li><li><strong>核心协议</strong>： <ul><li><strong>以太网（Ethernet）</strong>：局域网标准</li><li><strong>ARP</strong>：IP地址 → MAC地址解析</li><li><strong>PPP</strong>：拨号上网协议</li></ul></li><li><strong>典型设备</strong>：交换机、网卡</li></ul><h3 id="_2-网络层-ip层" tabindex="-1"><strong>（2）网络层（IP层）</strong> <a class="header-anchor" href="#_2-网络层-ip层" aria-label="Permalink to &quot;**（2）网络层（IP层）**&quot;">​</a></h3><ul><li><strong>功能</strong>：跨网络寻址和路由</li><li><strong>核心协议</strong>： <ul><li><strong>IP</strong>（IPv4/IPv6）：无连接的包交换协议</li><li><strong>ICMP</strong>：网络诊断（如<code>ping</code>）</li><li><strong>IGMP</strong>：组播管理</li></ul></li><li><strong>特点</strong>： <ul><li>不可靠（不保证送达）</li><li>无状态（每个包独立处理）</li></ul></li></ul><h3 id="_3-传输层" tabindex="-1"><strong>（3）传输层</strong> <a class="header-anchor" href="#_3-传输层" aria-label="Permalink to &quot;**（3）传输层**&quot;">​</a></h3><table tabindex="0"><thead><tr><th>协议</th><th>特点</th><th>应用场景</th></tr></thead><tbody><tr><td><strong>TCP</strong></td><td>可靠、面向连接、有流量控制</td><td>网页（HTTP）、邮件（SMTP）、文件传输（FTP）</td></tr><tr><td><strong>UDP</strong></td><td>不可靠、无连接、低延迟</td><td>视频流、DNS查询、在线游戏</td></tr></tbody></table><p><strong>TCP关键机制</strong>：</p><ul><li>三次握手建立连接</li><li>四次挥手断开连接</li><li>滑动窗口流量控制</li><li>超时重传</li></ul><h3 id="_4-应用层" tabindex="-1"><strong>（4）应用层</strong> <a class="header-anchor" href="#_4-应用层" aria-label="Permalink to &quot;**（4）应用层**&quot;">​</a></h3><table tabindex="0"><thead><tr><th>协议</th><th>端口</th><th>用途</th></tr></thead><tbody><tr><td>HTTP/HTTPS</td><td>80/443</td><td>网页浏览</td></tr><tr><td>FTP</td><td>20/21</td><td>文件传输</td></tr><tr><td>DNS</td><td>53</td><td>域名解析</td></tr><tr><td>SMTP</td><td>25</td><td>邮件发送</td></tr><tr><td>SSH</td><td>22</td><td>安全远程登录</td></tr></tbody></table><h2 id="_3-三次握手和四次挥手" tabindex="-1">3. 三次握手和四次挥手 <a class="header-anchor" href="#_3-三次握手和四次挥手" aria-label="Permalink to &quot;3. 三次握手和四次挥手&quot;">​</a></h2><p><code>TCP</code>是面向连接的 什么是面向连接，面向连接就是数据通讯的时候需要进行三次握手，断开通信的时候进行四次挥手</p><p>详细见： <a href="https://juejin.cn/post/7230450989592363068?searchId=202507220939320F6CB2B6AEFB4DA6758C" target="_blank" rel="noreferrer">https://juejin.cn/post/7230450989592363068?searchId=202507220939320F6CB2B6AEFB4DA6758C</a></p><h2 id="_4-常见问题" tabindex="-1"><strong>4. 常见问题</strong> <a class="header-anchor" href="#_4-常见问题" aria-label="Permalink to &quot;**4. 常见问题**&quot;">​</a></h2><h3 id="q1-为什么tcp-ip去掉了osi的会话-表示层" tabindex="-1"><strong>Q1：为什么TCP/IP去掉了OSI的会话/表示层？</strong> <a class="header-anchor" href="#q1-为什么tcp-ip去掉了osi的会话-表示层" aria-label="Permalink to &quot;**Q1：为什么TCP/IP去掉了OSI的会话/表示层？**&quot;">​</a></h3><ul><li>实际应用中，会话管理（如Cookie）和数据格式转换（如JSON）通常由应用程序直接处理。</li></ul><h3 id="q2-ip地址和mac地址的区别" tabindex="-1"><strong>Q2：IP地址和MAC地址的区别？</strong> <a class="header-anchor" href="#q2-ip地址和mac地址的区别" aria-label="Permalink to &quot;**Q2：IP地址和MAC地址的区别？**&quot;">​</a></h3><ul><li><strong>IP地址</strong>（网络层）：逻辑地址，可变化（如从Wi-Fi切换到4G）</li><li><strong>MAC地址</strong>（数据链路层）：物理地址，出厂固化</li></ul><h3 id="q3-tcp为什么是-可靠-的" tabindex="-1"><strong>Q3：TCP为什么是&quot;可靠&quot;的？</strong> <a class="header-anchor" href="#q3-tcp为什么是-可靠-的" aria-label="Permalink to &quot;**Q3：TCP为什么是&quot;可靠&quot;的？**&quot;">​</a></h3><p>通过以下机制保证：</p><ol><li>确认应答（ACK）</li><li>超时重传</li><li>数据排序</li><li>流量控制</li></ol><hr>',29)]))}const u=r(d,[["render",l]]);export{g as __pageData,u as default};
