import{_ as c,C as s,c as i,o as l,j as n,G as t,a}from"./chunks/framework.DEi4YP2n.js";const y=JSON.parse('{"title":"人性","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/其他/人性.md","filePath":"column/Poetry/其他/人性.md","lastUpdated":1759129977000}'),m={name:"column/Poetry/其他/人性.md"};function d(p,o,_,u,f,P){const r=s("ArticleMetadata"),e=s("HeroDisplay");return l(),i("div",null,[o[0]||(o[0]=n("h1",{id:"人性",tabindex:"-1"},[a("人性 "),n("a",{class:"header-anchor",href:"#人性","aria-label":'Permalink to "人性"'},"​")],-1)),t(r),o[1]||(o[1]=n("blockquote",null,[n("p",null,[a("当年意气风发的你，什么都听不进去；正如现在你的沉默寡言，什么都说不出口。"),n("br"),a(" 人性本就是不平的砖石，你却妄图用它堆砌高塔。")])],-1)),t(e,{content:["一见钟情不过是见色起意，日久生情也不过是权衡利弊。爱情这东西，始于颜值，陷于才华，忠于肉体，迷于声音。最后，折于物质，败于现实。"],notes:["这句话像一把解剖刀，把爱情削得只剩骨与血，冷得漂亮，也冷得伤人。"],main:""}),t(e,{content:["当初你本心无求，故能冷眼观，心中清明。如今有所求，求不得是苦，苦则生执，执则生妄。"],notes:["大道至简，无欲则刚。"],main:""}),t(e,{content:["真相湮没在甚嚣尘上中，随着流动的思维而左摇右摆。观念的水位没有警戒线，当群氓裹挟所谓正义以汹涌之势席卷而来，理智的堤坝经受不住感性的冲刷， 最先崩塌的，是疲惫而绝望的善良。 善良不可多得，亦不可多用。","没人能抛却立场布善，自以为是的悲悯不过是填补内心不安的粘合剂。当世界转动，分裂重组，世间万物都会陷进翻滚的浪潮里，毫无借力。 任凭沾染的颜色在脑海中调画一副即兴图，混淆的色彩再也无法区分。"],notes:["水清水浊，皆由你决定如何使用。不拒绝潮流，也不被它淹没；保持内心的清明，才是真正的“不随波逐流”。"],main:""}),t(e,{content:["思前想后固然是好，但不代表什么都不做，什么都不敢做。勇敢退后一步是中庸守成，再退一步是懦弱胆小。但是再进一步呢，又是过于冲动。总有个度，如何把握，如何面对后果。","要是你自己的事情，你比谁都不怕，比谁都勇敢。但若牵涉到旁人，立刻就有退缩之意，差那么一丁点就是逃避了。非得逼得人家跟你一样走投无路了，你又变得神挡杀神佛挡杀佛!"],notes:["我们恐惧的从来不是暴力本身，而是暴力背后那个需要为他人负责的“自我”。所谓勇敢，或许始于承认他人的绝境与自己有关，而不是等绝境把所有人都变成自己的镜像。"],main:""}),t(e,{content:["攀登的过程也许漫长，但巅峰的风景是值得的。当一个人的心中有着更高的山峰想去攀登时，他就不会在意脚下的泥沼。","他才可能用最平静的方式，去面对一般人难以承受的痛苦。","我们的征途是群山之巅。除了向上，在无其它方向可去。"],notes:["千淘万漉虽辛苦，吹尽狂沙始到金。","把目标交给峰顶，把痛苦交给脚步——从此眼里只有高度，心中没有退路。","你想拥有你从未有过的东西，就得去做你从未做过的事。"],main:""}),t(e,{content:["世界上最无耻最阴险最歹毒的赞美，就是用穷人的艰辛和苦难当做励志故事来愚弄底层人。","永远不要相信苦难是值得的，苦难就是苦难，苦难不会带来成功，苦难不值得追求，磨炼意志是因为苦难无法躲开。"],notes:["屋漏偏逢连夜雨，船迟又遇打头风。","真正的励志不该是“如何忍受苦难”，而应是“如何减少苦难”。"],main:""}),t(e,{content:["在欲望的漩涡当中，只有两种结果， 欲望不满足，你就痛苦； 欲望满足了，你就空虚。"],notes:["“生命就是一团欲望。欲望不能满足就会痛苦，满足了就会无聊。人生，就像钟摆，在痛苦和无聊之间摇摆。”"],main:""}),t(e,{content:["一开口便是民族、国家的安危、盛衰和荣辱，但对于自己子民的基本权利、自由和尊严，却从不思考，对于国内的各种不公、不义和无数的无辜、无助和无告的弱者，社会福利却漠不关心。"],notes:["可怜夜半虚前席，不问苍生问鬼神。","一个国家的盛衰，最终只能由其最弱成员的处境来衡量；若弱者被持续忽视，再响亮的口号也只是空心锣鼓。"],main:""}),t(e,{content:["对身边弱势群体的遭遇和不公现象视若无睹，却对远在天边素未谋面的陌生人恨之入骨，国人的爱国情怀一直都这么“伟大”。","和大人早就说过了，不管纪晓岚还是他被拉到菜市场砍头，老百姓都会拍手称快。所以我更想唾弃的是怒其不争，自扫门前雪和病态的爱国情怀。只会手捧圣贤书，骂骂当朝者而已。大部分国人愚昧无知是正常的。"],notes:["隔岸观火火更明，邻家粥沸寂无声。"],main:""})])}const b=c(m,[["render",d]]);export{y as __pageData,b as default};
