import{_ as i,C as o,c as s,o as d,j as t,G as e,a as n}from"./chunks/framework.DEi4YP2n.js";const P=JSON.parse('{"title":"借物喻人 移情于物","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/诗词相关/借物喻人-移情于物.md","filePath":"column/Poetry/诗词相关/借物喻人-移情于物.md","lastUpdated":1759201926000}'),m={name:"column/Poetry/诗词相关/借物喻人-移情于物.md"};function h(c,a,y,p,f,u){const l=o("ArticleMetadata"),r=o("PoetryDisplay");return d(),s("div",null,[a[0]||(a[0]=t("h1",{id:"借物喻人-移情于物",tabindex:"-1"},[n("借物喻人 移情于物 "),t("a",{class:"header-anchor",href:"#借物喻人-移情于物","aria-label":'Permalink to "借物喻人 移情于物"'},"​")],-1)),e(l),a[1]||(a[1]=t("h3",{id:"卜算子·咏梅",tabindex:"-1"},[n("卜算子·咏梅 "),t("a",{class:"header-anchor",href:"#卜算子·咏梅","aria-label":'Permalink to "卜算子·咏梅"'},"​")],-1)),e(r,{title:"卜算子·咏梅",author:"陆游",dynasty:"宋代",main:"坚贞自守，至死不渝",content:["驿外断桥边，寂寞开无主。已是黄昏独自愁，更着风和雨。","无意苦争春，一任群芳妒。零落成泥碾作尘，只有香如故。"],notes:["驿站外的断桥边，梅花孤单冷清的绽放着，无人过问。已是日落黄昏，她正独自忧愁感伤，却又遭到了风雨的摧残。","梅花并不想费尽心思与百花争奇斗艳，任凭百花嫉妒。即使凋零被碾作泥土，又化作尘土，它仍和往常一样散发出丝丝清香。"],moral:"诗人虽一生坎坷，备受排挤，但依然坚守爱国理想和高尚情操，至死不渝。"}),a[2]||(a[2]=t("h3",{id:"卜算子·咏梅-1",tabindex:"-1"},[n("卜算子·咏梅 "),t("a",{class:"header-anchor",href:"#卜算子·咏梅-1","aria-label":'Permalink to "卜算子·咏梅"'},"​")],-1)),e(r,{title:"卜算子·咏梅",author:"毛泽东",dynasty:"近现代",main:"坚韧不拔，无私奉献",content:["风雨送春归，飞雪迎春到。已是悬崖百丈冰，犹有花枝俏。","俏也不争春，只把春来报。待到山花烂漫时，她在丛中笑。"],notes:["风雨将春天送走了，飞雪又把春光迎来。正是悬崖结下百丈冰柱的时节，但仍然有花枝俏丽竞放。","俏丽但不掠春光之美，只是把春天的消息来报告。等到满山遍野开满鲜花之时，梅花却在花丛中欢笑。"],moral:"对逆境中坚韧不拔、乐观自信、无私奉献的精神赞美，也寄寓了对前途光明、胜利终将到来的坚定信念"}),a[3]||(a[3]=t("h3",{id:"减字木兰花·双龙对起",tabindex:"-1"},[n("减字木兰花·双龙对起 "),t("a",{class:"header-anchor",href:"#减字木兰花·双龙对起","aria-label":'Permalink to "减字木兰花·双龙对起"'},"​")],-1)),e(r,{title:"减字木兰花·双龙对起",author:"苏轼",dynasty:"宋代",main:"",content:["双龙对起，白甲苍髯烟雨里。疏影微香，下有幽人昼梦长。","湖风清软，双鹊飞来争噪晚。翠飐红轻，时下凌霄百尺英。"],notes:["两株古松冲天而起，铜枝铁干，屈伸偃仰，仿佛白甲苍髯的两条巨龙，张牙舞爪，在烟雨中飞腾。凌霄花的金红色花朵，掩映于一片墨绿苍翠之间，让人感到了一股淡淡的清香，一个和尚正躺在浓荫下的竹床上沉睡。","从湖上吹来的风，又清又软；一对喜鹊飞来树上，叽叽喳喳。只见在微风的摩挲之下，青翠的松枝伸展摇动，金红色的凌霄花儿微微颤动。"],moral:"透露出一种空灵、恬淡、与世无争的禅意。"}),a[4]||(a[4]=t("h3",{id:"墨梅·王冕",tabindex:"-1"},[n("墨梅·王冕 "),t("a",{class:"header-anchor",href:"#墨梅·王冕","aria-label":'Permalink to "墨梅·王冕"'},"​")],-1)),e(r,{title:"墨梅",author:"王冕",dynasty:"元代",main:"",content:["我家洗砚（yan）池头树，朵朵花开淡墨痕。","不要人夸好颜色，只留清气满乾坤。"],notes:["我家洗砚池边有一棵梅树，朵朵开放的梅花都像是用淡淡的墨汁点染而成。","它不需要别人夸奖颜色多么好看，只是要将清香之气弥漫在天地之间。"],moral:"不向世俗献媚、清高自守的坚贞品格"}),a[5]||(a[5]=t("h3",{id:"菊花·元稹",tabindex:"-1"},[n("菊花·元稹 "),t("a",{class:"header-anchor",href:"#菊花·元稹","aria-label":'Permalink to "菊花·元稹"'},"​")],-1)),e(r,{title:"菊花",author:"元稹",dynasty:"唐代",main:"",content:["秋丛绕舍似陶家，遍绕篱边日渐斜。","不是花中偏爱菊，此花开尽更无花。"],notes:["一丛一丛的秋菊环绕着房屋好似到了陶渊明的家。绕着篱笆观赏菊花，不知不觉太阳已经快落山了。","不是因为百花中偏爱菊花，只是因为菊花开过之后再无花可赏。"],moral:"诗人对菊花坚贞品格的赞美，也表达了自己不随波逐流的精神"}),a[6]||(a[6]=t("h3",{id:"蝉·虞世南",tabindex:"-1"},[n("蝉·虞世南 "),t("a",{class:"header-anchor",href:"#蝉·虞世南","aria-label":'Permalink to "蝉·虞世南"'},"​")],-1)),e(r,{title:"蝉",author:"虞世南",dynasty:"唐代",main:"",content:["垂緌饮清露，流响出疏桐（緌ruí:古代帽带的下垂部分）","居高声自远，非是藉秋风。"],notes:["蝉垂下像帽缨一样的触角吸吮着清澈甘甜的露水，连续不断的鸣叫声从稀疏的梧桐树枝间传出。","蝉正是因为在高处它的声音才能传得远，并非是凭借秋风的力量。"],moral:"诗人认为，品格高洁的人，不需要凭借外在的权势地位（秋风），自然能声名远扬。这是对自身修为的自信。"})])}const _=i(m,[["render",h],["__scopeId","data-v-6ce6caea"]]);export{P as __pageData,_ as default};
