import{_ as i,C as o,c as s,o as d,j as a,G as n,a as e}from"./chunks/framework.DEi4YP2n.js";const k=JSON.parse('{"title":"写尽相思 道尽别离","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/诗词相关/写尽相思-道尽别离.md","filePath":"column/Poetry/诗词相关/写尽相思-道尽别离.md","lastUpdated":1758696843000}'),m={name:"column/Poetry/诗词相关/写尽相思-道尽别离.md"};function h(y,t,b,u,f,P){const l=o("ArticleMetadata"),r=o("PoetryDisplay");return d(),s("div",null,[t[0]||(t[0]=a("h1",{id:"写尽相思-道尽别离",tabindex:"-1"},[e("写尽相思 道尽别离 "),a("a",{class:"header-anchor",href:"#写尽相思-道尽别离","aria-label":'Permalink to "写尽相思 道尽别离"'},"​")],-1)),n(l),t[1]||(t[1]=a("blockquote",null,[a("p",null,"晓梦无眠，人生短促，世事纷杂。晚风悠悠，痴人碎语，道尽情愁。")],-1)),t[2]||(t[2]=a("blockquote",null,[a("p",null,[e("与君相别离，不知何日是归期，我如朝露转瞬晞。"),a("br"),e(" 花散终有期，万代神樱立如昔，旧人归故里。")])],-1)),t[3]||(t[3]=a("h3",{id:"相见欢·林花谢了春红",tabindex:"-1"},[e("相见欢·林花谢了春红 "),a("a",{class:"header-anchor",href:"#相见欢·林花谢了春红","aria-label":'Permalink to "相见欢·林花谢了春红"'},"​")],-1)),n(r,{title:"相见欢·林花谢了春红",author:"李煜",dynasty:"五代",main:"春恨亡国，长恨东流",content:["林花谢了春红，太匆匆。无奈朝来寒雨晚来风。","胭脂泪，相留醉，几时重。自是人生长恨水长东。"],notes:["姹紫嫣红的花儿转眼已经凋谢，春光未免太匆忙。也是无可奈何啊，花儿怎么能经得起那凄风寒雨昼夜摧残呢？","着雨的林花娇艳欲滴好似那美人的胭脂泪。花儿和怜花人相互留恋，什么时候才能再重逢呢？人生令人遗憾的事情太多，就像那东逝的江水，不休不止，永无尽头。"]}),t[4]||(t[4]=a("h3",{id:"相见欢·无言独上西楼",tabindex:"-1"},[e("相见欢·无言独上西楼 "),a("a",{class:"header-anchor",href:"#相见欢·无言独上西楼","aria-label":'Permalink to "相见欢·无言独上西楼"'},"​")],-1)),n(r,{title:"相见欢·无言独上西楼",author:"李煜",dynasty:"五代",main:"独上西楼，寂寞锁秋",content:["无言独上西楼，月如钩。寂寞梧桐深院锁清秋。","剪不断，理还乱，是离愁。别是一般滋味在心头。"],notes:["默默无言，独自一人登上西楼，仰视天空，残月如钩。梧桐树寂寞地孤立院中，幽深的庭院被笼罩在清冷凄凉的秋色之中。","那剪也剪不断，理也理不清，让人心乱如麻的，正是亡国之愁。这样的离异思念之愁，而今在心头上却又是另一般不同的滋味。"]}),t[5]||(t[5]=a("h3",{id:"一剪梅·雨打梨花深闭门",tabindex:"-1"},[e("一剪梅·雨打梨花深闭门 "),a("a",{class:"header-anchor",href:"#一剪梅·雨打梨花深闭门","aria-label":'Permalink to "一剪梅·雨打梨花深闭门"'},"​")],-1)),n(r,{title:"一剪梅·雨打梨花深闭门",author:"唐寅",dynasty:"明代",main:"情深不寿，相思成疾",content:["雨打梨花深闭门，孤负青春，虚负青春。赏心乐事共谁论？花下销魂，月下销魂。","愁聚眉峰尽日颦，千点啼痕，万点啼痕。晓看天色暮看云，行也思君，坐也思君。"],notes:["深闭房门隔窗只听雨打梨花的声音，就这样辜负了青春年华，虚度了青春年华。纵然有欢畅愉悦的心情又能跟谁共享？花下也黯然神伤，月下也黯然神伤。","相思生愁整日都皱着眉，脸上留下千点泪痕，万点泪痕。从早晨到晚上一直在看着天色云霞，走路时想念你啊，坐着时也是想念你！"]}),t[6]||(t[6]=a("h3",{id:"一剪梅·红藕香残玉簟秋",tabindex:"-1"},[e("一剪梅·红藕香残玉簟秋 "),a("a",{class:"header-anchor",href:"#一剪梅·红藕香残玉簟秋","aria-label":'Permalink to "一剪梅·红藕香残玉簟秋"'},"​")],-1)),n(r,{title:"一剪梅·红藕香残玉簟秋",author:"李清照",dynasty:"宋代",main:"相思刻骨，孤独难捱",content:["红藕香残玉簟秋。轻解罗裳，独上兰舟。云中谁寄锦书来？雁字回时，月满西楼。","花自飘零水自流。一种相思，两处闲愁。此情无计可消除，才下眉头，却上心头。"],notes:["粉色的荷花已经凋谢，幽香也已消散，光滑如玉的竹席带着秋的凉意。解开绫罗裙，换着便装，独自登上小船。仰头凝望远天，那白云舒卷处，谁会将锦书寄来？雁群飞回来时，月光已经洒满了西楼。","落花独自飘零，水独自流淌。彼此都在思念对方，可又不能互相倾诉，只好各在一方独自愁闷着。这相思的愁苦实在无法排遣，刚从微蹙的眉间消失，又隐隐缠绕上了心头。"]}),t[7]||(t[7]=a("h3",{id:"玉楼春·春恨",tabindex:"-1"},[e("玉楼春·春恨 "),a("a",{class:"header-anchor",href:"#玉楼春·春恨","aria-label":'Permalink to "玉楼春·春恨"'},"​")],-1)),n(r,{title:"玉楼春·春恨",author:"晏殊",dynasty:"宋代",main:"相思无尽，离恨绵绵",content:["绿杨芳草长亭路。年少抛人容易去。楼头残梦五更钟，花底离愁三月雨。（离愁：离情）","无情不似多情苦。一寸还成千万缕。天涯地角有穷时，只有相思无尽处。"],notes:["在杨柳依依、芳草萋萋的长亭古道上，年少的人总是能轻易的抛弃送别之人登程远去。楼头传来的五更钟声惊醒了离人残梦，花底飘洒的三月春雨增添了心中的愁思。","无情人哪里懂得多情的人的苦恼，一寸相思愁绪竟化作了万缕千丝。天涯地角再远也有穷尽终了那一天，只有那相思是没有尽头，永不停止。"]}),t[8]||(t[8]=a("h3",{id:"江陵愁望寄子安",tabindex:"-1"},[e("江陵愁望寄子安 "),a("a",{class:"header-anchor",href:"#江陵愁望寄子安","aria-label":'Permalink to "江陵愁望寄子安"'},"​")],-1)),n(r,{title:"江陵愁望寄子安",author:"鱼玄机",dynasty:"唐代",main:"相思无尽，望断江陵",content:["枫叶千枝复万枝，江桥掩映暮帆迟。","忆君心似西江水，日夜东流无歇时。"],notes:["深秋枫树千枝万枝，江桥掩映枫林之中。日已垂暮，仍不见等候之人乘船归来。","思念你的心情就像那西江流水，日日夜夜向东流去，不曾停歇。"]}),t[9]||(t[9]=a("h3",{id:"十二月过尧民歌·别情",tabindex:"-1"},[e("十二月过尧民歌·别情 "),a("a",{class:"header-anchor",href:"#十二月过尧民歌·别情","aria-label":'Permalink to "十二月过尧民歌·别情"'},"​")],-1)),n(r,{title:"十二月过尧民歌·别情",author:"王实甫",dynasty:"元代",main:"新痕压旧，愁上加愁",content:["自别后遥山隐隐，更那堪远水粼粼。见杨柳飞绵滚滚，对桃花醉脸醺醺。透内阁香风阵阵，掩重门暮雨纷纷。","怕黄昏忽地又黄昏，不销魂怎地不销魂。新啼痕压旧啼痕，断肠人忆断肠人。今春香肌瘦几分？缕带宽三寸。"],notes:["自从和你分别后，望不尽远山层叠隐约迷濛，更难忍受清粼粼的江水奔流不回，看见柳絮纷飞绵涛滚滚，对着璀璨桃花痴醉得脸生红晕。闺房里透出香风一阵阵，重门深掩到黄昏，听雨声点点滴滴敲打房门。","怕黄昏到来，黄昏偏偏匆匆来临，不想失魂落魄又叫人怎能不失魂伤心？旧的泪痕还未干透，又添了新的泪痕，断肠人常挂记着断肠人。要知道今年春天，我的身体瘦了多少，看衣带都宽出了三寸。"]}),t[10]||(t[10]=a("h3",{id:"赠邻女",tabindex:"-1"},[e("赠邻女 "),a("a",{class:"header-anchor",href:"#赠邻女","aria-label":'Permalink to "赠邻女"'},"​")],-1)),n(r,{title:"赠邻女",author:"鱼玄机",dynasty:"唐代",main:"痴情错付，悔断愁肠",content:["羞日遮罗袖，愁春懒起妆。","易求无价宝，难得有心郎。","枕上潜垂泪，花间暗断肠。","自能窥宋玉，何必恨王昌？"],notes:["白天总是用衣袖遮住脸，春日里更添惆怅，连起来装扮都不愿意。","无价之宝容易求得，而有情的郎君实在难以找到。","无论是睡觉时，还是赏花时，都会暗暗垂泪，痛断肝肠。","既然已有了这样的才貌，宋玉这样的才子也可以求得的，又何必去遗憾王昌这样的才子？"]}),t[11]||(t[11]=a("h3",{id:"竹枝词·山桃红花满上头",tabindex:"-1"},[e("竹枝词·山桃红花满上头 "),a("a",{class:"header-anchor",href:"#竹枝词·山桃红花满上头","aria-label":'Permalink to "竹枝词·山桃红花满上头"'},"​")],-1)),n(r,{title:"竹枝词·山桃红花满上头",author:"刘禹锡",dynasty:"唐代",main:"郎心易变，侬愁如水",content:["山桃红花满上头，蜀江春水拍山流。","花红易衰似郎意，水流无限似侬愁。"],notes:["春天，鲜红的野桃花开满山头，蜀江的江水拍打着山崖向东流去。","容易凋零的桃花就像郎君的情意，这源源不断的江水就像我无限的忧愁。"]}),t[12]||(t[12]=a("h3",{id:"相思怨",tabindex:"-1"},[e("相思怨 "),a("a",{class:"header-anchor",href:"#相思怨","aria-label":'Permalink to "相思怨"'},"​")],-1)),n(r,{title:"相思怨",author:"刘治",dynasty:"唐代",main:"海水有涯，相思无畔",content:["人道海水深，不抵相思半。","海水尚有涯，相思渺无畔。","携琴上高楼，楼虚月华满。","弹著相思曲，弦肠一时断。"],notes:["人们说海水最深，我说海深度不及我思念的一半。","海水的广袤尚有边际，相思则是无边无际。","携琴登上高楼，已是人去楼空，只有一片月光。","弹奏一首相思曲，让相思肠愁随着弦一起断了。"]}),t[13]||(t[13]=a("h3",{id:"鹧鸪天·醉拍春衫惜旧香",tabindex:"-1"},[e("鹧鸪天·醉拍春衫惜旧香 "),a("a",{class:"header-anchor",href:"#鹧鸪天·醉拍春衫惜旧香","aria-label":'Permalink to "鹧鸪天·醉拍春衫惜旧香"'},"​")],-1)),n(r,{title:"鹧鸪天·醉拍春衫惜旧香",author:"晏几道",dynasty:"宋代",main:"旧香犹在，人隔天涯",content:["醉拍春衫惜旧香。天将离恨恼疏狂。年年陌上生秋草，日日楼中到夕阳。","云渺渺，水茫茫。征人归路许多长。相思本是无凭语，莫向花笺费泪行。"],notes:["借着醉意拍春衫，回想着，旧日春衫上的香。天将离愁与别恨，折磨我这疏狂人。路上年年生秋草，楼中日日进夕阳。","登楼望；云渺渺，水茫茫。征人归路在哪方。相思话语无诉处，又何必，写在信纸上，费了泪千行。"]}),t[14]||(t[14]=a("h3",{id:"鹧鸪天·重过阊门万事非",tabindex:"-1"},[e("鹧鸪天·重过阊门万事非 "),a("a",{class:"header-anchor",href:"#鹧鸪天·重过阊门万事非","aria-label":'Permalink to "鹧鸪天·重过阊门万事非"'},"​")],-1)),n(r,{title:"鹧鸪天·醉拍春衫惜旧香",author:"贺铸",dynasty:"宋代",main:"物是人非，悼亡之痛",content:["重过阊门万事非。同来何事不同归。梧桐半死清霜后，头白鸳鸯失伴飞。","原上草，露初晞。旧栖新垅两依依。空床卧听南窗雨，谁复挑灯夜补衣。"],notes:["再次来到苏州，只觉得物是人非。曾与我同来的妻子为什么不能与我同归呢？自己如同霜打的梧桐半死半生，又像失伴的鸳鸯，孤独倦飞。","原野里绿草嫩叶上的露珠刚刚被晒干。我流连于旧日同住的居室，又徘徊于垄上的新坟。躺在空荡荡的床上，听着窗外的凄风苦雨，从今以后还有谁替我在深夜挑灯缝补衣衫呢？"]}),t[15]||(t[15]=a("h3",{id:"离思五首·其四",tabindex:"-1"},[e("离思五首·其四 "),a("a",{class:"header-anchor",href:"#离思五首·其四","aria-label":'Permalink to "离思五首·其四"'},"​")],-1)),n(r,{title:"离思五首·其四",author:"元稹",dynasty:"唐代",main:"曾经沧海，除却巫山",content:["曾经沧海难为水，除却巫山不是云。","取次花丛懒回顾，半缘修道半缘君。"],notes:["经历过波澜壮阔的大海，别处的水再也不值得一观。陶醉过巫山的云雨的梦幻，别处的风景就不称之为云雨了。","即使身处万花丛中，我也懒得回头顾盼；这缘由，一半是因为修道人的清心寡欲，一半是因为曾经拥有过的你。"]})])}const x=i(m,[["render",h],["__scopeId","data-v-35ed0b0c"]]);export{k as __pageData,x as default};
