import{_ as r,C as s,c as l,o as d,j as n,G as t,a}from"./chunks/framework.DEi4YP2n.js";const x=JSON.parse('{"title":"名句总结","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/诗词相关/名句总结.md","filePath":"column/Poetry/诗词相关/名句总结.md","lastUpdated":1759201926000}'),i={name:"column/Poetry/诗词相关/名句总结.md"};function m(p,o,_,u,f,P){const c=s("ArticleMetadata"),e=s("FamousDisplay");return d(),l("div",null,[o[0]||(o[0]=n("h1",{id:"名句总结",tabindex:"-1"},[a("名句总结 "),n("a",{class:"header-anchor",href:"#名句总结","aria-label":'Permalink to "名句总结"'},"​")],-1)),t(c),o[1]||(o[1]=n("blockquote",null,[n("p",null,[a("见证者，为见证而来。铭记者，因铭记而生。"),n("br"),a(" 见证者携光而来，铭记者化泪为文；一次凝视，一次书写，共同让刹那成为永恒。")])],-1)),t(e,{content:["我见青山多妩媚，料青山见我应如是。情与貌，略相似。"],notes:["我看那青山潇洒多姿，想必青山看我也是一样。不论情怀还是外貌，都非常相似。"]}),t(e,{content:["自在飞花轻似梦，无边丝雨细如愁。"],notes:["飞花轻舞如虚无缥缈的梦境，丝丝细雨落下如同我的忧愁。"]}),t(e,{content:["沙上并禽池上暝，云破月来花弄影。重重帘幕密遮灯，风不定，人初静，明日落红应满径。"],notes:["天黑后，鸳鸯在池边并眠，花枝在月光下舞弄自己的倩影。一重重帘幕密密地遮住灯光，风还没有停止，人声已安静，明天落花应该会铺满园中小径。"]}),t(e,{content:["莫道桑榆晚，为霞尚满天。"],notes:["不要说日落时光照桑榆树端已近傍晚，它的霞光余辉照样可以映红满天。","表达坚韧不拔的意志和乐观积极的精神。"]}),t(e,{content:["兰生幽谷，不为莫服而不芳。舟在江海，不为莫乘而不浮。君子行义，不为莫知而止休。"],notes:["兰花生长在无人的山谷，不会因为没人佩戴而不散发芳香；船在江河湖海上，不会因为没有人乘坐而不浮在水上；君子行使自己的道义，不因没有人理解而停止。"]}),t(e,{content:["合抱之木，生于毫末；九层之台，起于累土；千里之行，始于足下。"],notes:["合抱的大树，生长于细小的萌芽；极高的高台，筑起于每一堆泥土；千里的远行，是从脚下第一步开始走出来的"]}),t(e,{content:["居逆境中，周身皆针砭药石，砥节砺行而不觉；处顺境内，眼前尽兵刃戈矛，销膏靡骨而不知。"],notes:["处在不顺利的环境中，就好比全身都扎着针、敷着药，在不知不觉中磨练着意志、培养着高尚的品行。处在优越的环境中，就好比被各种兵器所包围，不知不觉就被掏空了身体，消磨了意志。"]}),t(e,{content:["月缺不改光，剑折不改刚。月缺魄易满，剑折铸复良。"],notes:["月亮不会因为缺损而改变它的光辉，宝剑不会因为折断而改变它刚硬的本质。月亮缺了它的辉光很容易会盈满，宝剑折断经过重铸会再次复原。"]}),t(e,{content:["吾十有五而志于学，三十而立，四十而不惑，五十而知天命，六十而耳顺，七十而从心所欲，不逾矩。"],notes:["我十五岁就立志学习，三十岁就能立足于世，四十岁遇到事情不再感到困惑，五十岁就知道哪些是不能为人力支配的事情而乐知天命，六十岁时能听得进各种不同的意见，七十岁可以随心所欲、收放自如，却又不超出规矩。​"]}),t(e,{content:["看似寻常最奇崛，成如容易却艰辛。"],notes:["看似寻常的作品实际最奇崛，写成好像容易却饱含艰辛。"]}),t(e,{content:["时人不识凌云木，直待凌云始道高。"],notes:["世上的人不认识这是将来可以高入云霄的树木，一直要等到它已经高入云霄了，才承认它的伟岸。"]}),t(e,{content:["此身天地一蘧(qú)庐，世事消磨绿鬓(bìn)疏。毕竟几人真得鹿，不知终日梦为鱼。"],notes:["天地一庐，人生一梦，得鹿失鹿，终归是枕上鱼。"]}),t(e,{content:["满堂花醉三千客，一剑霜寒十四州。"],notes:["满堂的花香熏醉了无数宾客，一把利剑横扫两浙十四州。"]}),t(e,{content:["在天愿作比翼鸟，在地愿为连理枝。","天长地久有时尽，此恨绵绵无绝期。"],notes:["在天上，愿意化作那比翼双飞的鸟儿；在地上，愿意成为那枝干相连的树木。","即使是那天长地久的爱情，也总会有结束的时候，然而这生死离别的悲恨，却永远没有终止的期限。"]})])}const y=r(i,[["render",m]]);export{x as __pageData,y as default};
