import{_ as c,C as s,c as r,o as l,j as o,G as t,a as d}from"./chunks/framework.DEi4YP2n.js";const y=JSON.parse('{"title":"名句总结","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/诗词相关/名句总结.md","filePath":"column/Poetry/诗词相关/名句总结.md","lastUpdated":1764230157000}'),i={name:"column/Poetry/诗词相关/名句总结.md"};function m(p,n,_,u,f,P){const a=s("ArticleMetadata"),e=s("FamousDisplay");return l(),r("div",null,[n[0]||(n[0]=o("h1",{id:"名句总结",tabindex:"-1"},[d("名句总结 "),o("a",{class:"header-anchor",href:"#名句总结","aria-label":'Permalink to "名句总结"'},"​")],-1)),t(a),n[1]||(n[1]=o("blockquote",null,[o("p",null,"见证者，为见证而来。铭记者，因铭记而生。见证者携光而来，铭记者化泪为文。")],-1)),t(e,{content:["我见青山多妩媚，料青山见我应如是。情与貌，略相似。"],notes:["我看那青山潇洒多姿，想必青山看我也是一样。不论情怀还是外貌，都非常相似。"]}),t(e,{content:["自在飞花轻似梦，无边丝雨细如愁。"],notes:["飞花轻舞如虚无缥缈的梦境，丝丝细雨落下如同我的忧愁。"]}),t(e,{content:["醉后不知天在水，满船清梦压星河。"],notes:["醉里忘却了天幕正倒扣在水面，一船轻甜的清梦，竟把万点星河压得低垂。"]}),t(e,{content:["经霜自有凌云意，勿做依人媚骨花。我自泠然御风立，开落由天不管春。"],notes:["历经霜寒，自存凌云之志；不做那依附他人、媚态十足的花。我独自清冷如风中挺立，花开花谢听凭天意，从不迎合春光。"]}),t(e,{content:["落花人独立，微雨燕双飞。寓目魂将断，经年梦亦非。"],notes:["独自一人站在落花前，细雨纷纷，燕子成双飞去。看到这一幕幕只觉得魂魄都将要失去，经过很多年梦境也不一样了。"]}),t(e,{content:["沙上并禽池上暝，云破月来花弄影。重重帘幕密遮灯，风不定，人初静，明日落红应满径。"],notes:["天黑后，鸳鸯在池边并眠，花枝在月光下舞弄自己的倩影。一重重帘幕密密地遮住灯光，风还没有停止，人声已安静，明天落花应该会铺满园中小径。"]}),t(e,{content:["莫道桑榆晚，为霞尚满天。"],notes:["不要说日落时光照桑榆树端已近傍晚，它的霞光余辉照样可以映红满天。","坚韧不拔的意志和乐观积极的精神。"]}),t(e,{content:["翩若踏惊鸿，翠衣舞游龙。轻云蔽明月，流风赞回雪。"],notes:["翩然起步，似惊鸿踏水；翠袖翻飞，若游龙穿空。淡云悄悄替月拢上面纱，疾风暗暗为雪唱出回声。"]}),t(e,{content:["东篱对酒日曾多，匣里清霜负旧疴。一骑白马翩入梦，不寻酒肆问干戈。"],notes:["当年在东篱下对酒高歌，那样的日子确实不少；匣中宝剑积了清霜，也一并扛起我多年的病与愁。忽而一骑白马翩然闯进梦里，我不再找酒肆，却想去打听人间的刀兵与战祸。"]}),t(e,{content:["竹林深处听风语，月下孤影舞清歌。 剑指苍穹问星汉，一梦长谢世间磨。"],notes:["在幽深的竹林里，静听风低语如诉；月光下，一道孤影伴着清歌起舞。举剑直指苍天，追问那星河何处；一梦醒来，早已看淡尘世的风波苦楚。"]}),t(e,{content:["天高云淡，望断南飞雁。不到长城非好汉，屈指行程二万。"],notes:["长空高阔白云清朗，南飞的大雁已飞到了天际尽头。不登临目的地绝不是英雄，算下来已征战了二万里的路途。"]}),t(e,{content:["兰生幽谷，不为莫服而不芳。舟在江海，不为莫乘而不浮。君子行义，不为莫知而止休。"],notes:["兰花生长在无人的山谷，不会因为没人佩戴而不散发芳香；船在江河湖海上，不会因为没有人乘坐而不浮在水上；君子行使自己的道义，不因没有人理解而停止。"]}),t(e,{content:["合抱之木，生于毫末；九层之台，起于累土；千里之行，始于足下。"],notes:["合抱的大树，生长于细小的萌芽；极高的高台，筑起于每一堆泥土；千里的远行，是从脚下第一步开始走出来的"]}),t(e,{content:["居逆境中，周身皆针砭药石，砥节砺行而不觉；处顺境内，眼前尽兵刃戈矛，销膏靡骨而不知。"],notes:["处在不顺利的环境中，就好比全身都扎着针、敷着药，在不知不觉中磨练着意志、培养着高尚的品行。处在优越的环境中，就好比被各种兵器所包围，不知不觉就被掏空了身体，消磨了意志。"]}),t(e,{content:["月缺不改光，剑折不改刚。月缺魄易满，剑折铸复良。"],notes:["月亮不会因为缺损而改变它的光辉，宝剑不会因为折断而改变它刚硬的本质。月亮缺了它的辉光很容易会盈满，宝剑折断经过重铸会再次复原。"]}),t(e,{content:["吾十有五而志于学，三十而立，四十而不惑，五十而知天命，六十而耳顺，七十而从心所欲，不逾矩。"],notes:["我十五岁就立志学习，三十岁就能立足于世，四十岁遇到事情不再感到困惑，五十岁就知道哪些是不能为人力支配的事情而乐知天命，六十岁时能听得进各种不同的意见，七十岁可以随心所欲、收放自如，却又不超出规矩。​"]}),t(e,{content:["时人不识凌云木，直待凌云始道高。"],notes:["世上的人不认识这是将来可以高入云霄的树木，一直要等到它已经高入云霄了，才承认它的伟岸。","看似寻常最奇崛，成如容易却艰辛。"]}),t(e,{content:["满堂花醉三千客，一剑霜寒十四州。"],notes:["满堂的花香熏醉了无数宾客，一把利剑横扫两浙十四州。"]}),t(e,{content:["在天愿作比翼鸟，在地愿为连理枝。","天长地久有时尽，此恨绵绵无绝期。"],notes:["在天上，愿意化作那比翼双飞的鸟儿；在地上，愿意成为那枝干相连的树木。","即使是那天长地久的爱情，也总会有结束的时候，然而这生死离别的悲恨，却永远没有终止的期限。"]})])}const h=c(i,[["render",m]]);export{y as __pageData,h as default};
