import{_ as l,C as o,c as s,o as d,j as t,G as e,a as n}from"./chunks/framework.DEi4YP2n.js";const x=JSON.parse('{"title":"田园风光","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/诗词相关/田园风光.md","filePath":"column/Poetry/诗词相关/田园风光.md","lastUpdated":1765185981000}'),h={name:"column/Poetry/诗词相关/田园风光.md"};function m(y,a,b,u,f,P){const i=o("ArticleMetadata"),r=o("PoetryDisplay");return d(),s("div",null,[a[0]||(a[0]=t("h1",{id:"田园风光",tabindex:"-1"},[n("田园风光 "),t("a",{class:"header-anchor",href:"#田园风光","aria-label":'Permalink to "田园风光"'},"​")],-1)),e(i),a[1]||(a[1]=t("blockquote",null,[t("p",null,"以朴为笔，写尽尘外之欢，归田即归心。")],-1)),a[2]||(a[2]=t("h3",{id:"相见欢·林花谢了春红",tabindex:"-1"},[n("相见欢·林花谢了春红 "),t("a",{class:"header-anchor",href:"#相见欢·林花谢了春红","aria-label":'Permalink to "相见欢·林花谢了春红"'},"​")],-1)),e(r,{title:"归园田居·其一",author:"陶渊明",dynasty:"魏晋",main:"挣脱樊笼，返璞归真",content:["少无适俗韵，性本爱丘山。误入尘网中，一去三十年。","羁鸟恋旧林，池鱼思故渊。开荒南野际，守拙归园田。","方宅十余亩，草屋八九间。榆柳荫后檐，桃李罗堂前。","暧暧远人村，依依墟里烟。狗吠深巷中，鸡鸣桑树颠。","户庭无尘杂，虚室有余闲。久在樊笼里，复得返自然。"],notes:["年少时就没有适应世俗的性情，生来就喜爱大自然的风物。错误地陷落到世俗的种种束缚中，转眼间远离田园已十余年(十三年)。","笼中之鸟怀念以前生活的树林，池塘之鱼思念旧日嬉戏的深潭。我愿到南面的田野去开荒，依着拙朴的心性回家耕种田园。","围绕着房宅方圆有十余亩地，还有八九间茅草屋。生长茂盛的榆树柳树遮蔽了后屋檐，桃树李树整齐的罗列在屋前。","远处邻村的屋舍依稀可见，村落上方轻柔而缓慢的飘升着袅袅炊烟。深深的街巷中传来了几声狗吠，桑树顶端有雄鸡不停啼鸣。","庭院内没有尘俗杂事烦扰，静室里有的是安适悠闲。久久困于官场生活，毫无自由，我今日总算又归耕园田。"]}),a[3]||(a[3]=t("h3",{id:"饮酒·其五",tabindex:"-1"},[n("饮酒·其五 "),t("a",{class:"header-anchor",href:"#饮酒·其五","aria-label":'Permalink to "饮酒·其五"'},"​")],-1)),e(r,{title:"饮酒·其五",author:"陶渊明",dynasty:"魏晋",main:"庐在人境，心远自偏",content:["结庐在人境，而无车马喧。问君何能尔？心远地自偏。","采菊东篱下，悠然见南山。山气日夕佳，飞鸟相与还。","此中有真意，欲辨已忘言。"],notes:["将房屋建造在人来人往的地方，却不会受到世俗交往的喧扰。问我为什么能这样，只要心中所想远离世俗，自然就会觉得所处地方僻静了。","在东篱之下采摘菊花，悠然间，那远处的南山映入眼帘。傍晚时分南山景致甚佳，雾气峰间缭绕，飞鸟结伴而还。","这里面蕴含着人生的真正意义，想要分辨清楚，却已忘了怎样表达。"]}),a[4]||(a[4]=t("h3",{id:"归去来兮辞·节选",tabindex:"-1"},[n("归去来兮辞·节选 "),t("a",{class:"header-anchor",href:"#归去来兮辞·节选","aria-label":'Permalink to "归去来兮辞·节选"'},"​")],-1)),e(r,{title:"归去来兮辞·节选",author:"陶渊明",dynasty:"魏晋",main:"厌弃官缚，归田怡悦",content:["归去来兮，田园将芜胡不归？既自以心为形役，奚惆怅而独悲？悟已往之不谏，知来者之可追。实迷途其未远，觉今是而昨非。舟遥遥以轻飏，风飘飘而吹衣。问征夫以前路，恨晨光之熹微。","   乃瞻衡宇，载欣载奔。僮仆欢迎，稚子候门。三径就荒，松菊犹存。携幼入室，有酒盈樽。引壶觞以自酌，眄庭柯以怡颜。倚南窗以寄傲，审容膝之易安。园日涉以成趣，门虽设而常关。策扶老以流憩，时矫首而遐观。云无心以出岫，鸟倦飞而知还。景翳翳以将入，抚孤松而盘桓。","   归去来兮，请息交以绝游。世与我而相违，复驾言兮焉求？悦亲戚之情话，乐琴书以消忧。农人告余以春及，将有事于西畴。或命巾车，或棹孤舟。既窈窕以寻壑，亦崎岖而经丘。木欣欣以向荣，泉涓涓而始流。善万物之得时，感吾生之行休。"],notes:["回家去吧！田园快要荒芜了，为什么不回去呢？既然自己使心为身所驱役，又何必怅惘而独自悲戚呢？我已明悟过去的错误已经不可挽回，未来的事还来得及补救。我确实走入了迷途，但还不算太远，已觉悟如今的做法是对的，而曾经的行为是错的。船在水面上轻轻地飘荡着前进，微风吹拂着衣裳，衣袂翩翩。向行人打听前面的路程，遗憾的是天刚刚放亮。","刚刚看到自己简陋的家门，心中欣喜，奔跑过去。家中童仆欢喜地前来迎接，孩子们守候在门前。院子里的小路快要荒芜了，松菊还长在那里。带着孩子们进了屋，美酒已经盛满了酒樽。我端起酒壶酒杯自斟自饮，看看院子里的树木，觉得很愉快；倚着南窗寄托我的傲世之情，深知这狭小之地容易使我心安。天天到园里行走，自成一种乐趣，小园的门经常地关闭着；拄着拐杖出去走走，随时随地休息，时时抬头望着远方（的天空）。云气自然而然地从山峰飘浮而出，倦飞的鸟儿也知道飞回巢中；日光暗淡，太阳快要落下去了，我手抚着孤松徘徊着不忍离去。","回家去吧！请让我同外界断绝交游。世事与我所想的相违背，还要驾车出去追求什么？以亲人间的知心话为愉悦，以弹琴读书为乐来消除忧愁；农夫告诉我春天到了，西边田野里要开始耕种了。有时驾着有布篷的小车，有时划着一条小船，有时经过幽深曲折的山谷，有时走过高低不平的山路。草木茂盛，细水缓流，（我）羡慕自然界的万物一到春天便及时生长茂盛，感叹自己的一生行将结束。"]}),a[5]||(a[5]=t("h3",{id:"山居秋暝",tabindex:"-1"},[n("山居秋暝 "),t("a",{class:"header-anchor",href:"#山居秋暝","aria-label":'Permalink to "山居秋暝"'},"​")],-1)),e(r,{title:"山居秋暝",author:"王维",dynasty:"唐代",main:"秋山夜雨，清幽怡景",content:["空山新雨后，天气晚来秋。明月松间照，清泉石上流。","竹喧归浣女，莲动下渔舟。随意春芳歇，王孙自可留。"],notes:["新雨过后山谷里空旷清新，初秋傍晚的天气特别凉爽。明月映照着幽静的松林间，清澈泉水在山石上淙淙淌流。","竹林里传来喧闹声，知是少女洗衣归来，莲叶轻摇知是上游荡下轻舟。任凭春天的花草消失，眼前的秋景足以让我流连久居。"]}),a[6]||(a[6]=t("h3",{id:"鹿柴",tabindex:"-1"},[n("鹿柴 "),t("a",{class:"header-anchor",href:"#鹿柴","aria-label":'Permalink to "鹿柴"'},"​")],-1)),e(r,{title:"鹿柴",author:"王维",dynasty:"唐代",main:"空山深林，返照寂景",content:["空山不见人，但闻人语响。","返景入深林，复照青苔上。"],notes:["幽静的山谷里看不见人，只听到人说话的声音。","落日余光映入了深林，又照在幽暗处的青苔上。"]}),a[7]||(a[7]=t("h3",{id:"渭川田家",tabindex:"-1"},[n("渭川田家 "),t("a",{class:"header-anchor",href:"#渭川田家","aria-label":'Permalink to "渭川田家"'},"​")],-1)),e(r,{title:"渭川田家",author:"王维",dynasty:"唐代",main:"斜光照墟，归牧皆闲",content:["斜阳照墟落，穷巷牛羊归。野老念牧童，倚杖候荆扉。","雉雊麦苗秀，蚕眠桑叶稀。田夫荷锄至，相见语依依。","即此羡闲逸，怅然吟式微。"],notes:["夕阳的余晖洒向村庄，牛羊沿着深巷纷纷回归。村中老人惦念着放牧的孙儿，倚着拐杖在柴门边等候。","野鸡鸣叫，麦子秀穗，蚕像睡觉了一样，桑叶也变得稀少。农夫们三三两两扛着锄头归来，在田间小道上偶然相遇，亲切絮语，乐而忘归。","在这种时刻如此闲情逸致怎不叫我羡慕？我不禁怅然地吟起《式微》。"]}),a[8]||(a[8]=t("h3",{id:"过故人庄",tabindex:"-1"},[n("过故人庄 "),t("a",{class:"header-anchor",href:"#过故人庄","aria-label":'Permalink to "过故人庄"'},"​")],-1)),e(r,{title:"过故人庄",author:"孟浩然",dynasty:"唐代",main:"绿树村合，把酒话桑",content:["故人具鸡黍，邀我至田家。绿树村边合，青山郭外斜。","开轩面场圃，把酒话桑麻。待到重阳日，还来就菊花。"],notes:["老朋友准备丰盛的饭菜，邀请我到他田舍做客。翠绿的树林围绕着村落，一脉青山在城郭外隐隐横斜。","推开窗户面对谷场菜园，共饮美酒，闲谈农务。等到九九重阳节到来时，我还要来这里观赏菊花。"]}),a[9]||(a[9]=t("h3",{id:"宿建德江",tabindex:"-1"},[n("宿建德江 "),t("a",{class:"header-anchor",href:"#宿建德江","aria-label":'Permalink to "宿建德江"'},"​")],-1)),e(r,{title:"宿建德江",author:"孟浩然",dynasty:"唐代",main:"江清月近，客愁夜泊",content:["移舟泊烟渚，日暮客愁新。","野旷天低树，江清月近人。"],notes:["把船停泊在烟雾弥漫的沙洲旁，日落时新愁又涌上了心头。","原野无边无际，远处的天空比近处的树林还要低；江水清清，明月仿似更与人相亲。"]}),a[10]||(a[10]=t("h3",{id:"四时田园杂兴·其二十五",tabindex:"-1"},[n("四时田园杂兴·其二十五 "),t("a",{class:"header-anchor",href:"#四时田园杂兴·其二十五","aria-label":'Permalink to "四时田园杂兴·其二十五"'},"​")],-1)),e(r,{title:"四时田园杂兴·其二十五",author:"范成大",dynasty:"宋代",main:"梅黄杏肥，田园生趣",content:["梅子金黄杏子肥，麦花雪白菜花稀。","日长篱落无人过，惟有蜻蜓蛱蝶飞。"],notes:["初夏时节，金黄的梅子挂满枝头，杏子也变得鲜亮饱满，田里麦穗扬着白花，油菜花差不多落尽正在结籽。","白天变长了，大家都在田间忙碌，篱落边无人经过，只有蜻蜓和蝴蝶绕着篱笆飞来飞去。"]}),a[11]||(a[11]=t("h3",{id:"江雪",tabindex:"-1"},[n("江雪 "),t("a",{class:"header-anchor",href:"#江雪","aria-label":'Permalink to "江雪"'},"​")],-1)),e(r,{title:"江雪",author:"柳宗元",dynasty:"唐代",main:"寒江独钓，万寂一翁",content:["千山鸟飞绝，万径人踪灭。","孤舟蓑笠翁，独钓寒江雪。"],notes:["所有山上飞鸟的身影已经绝迹，所有道路都不见人的踪迹。","江面孤舟上，一位披戴着蓑笠的老翁，独自在漫天风雪中垂钓。"]}),a[12]||(a[12]=t("h3",{id:"登柳州城楼寄漳汀封连四州",tabindex:"-1"},[n("登柳州城楼寄漳汀封连四州 "),t("a",{class:"header-anchor",href:"#登柳州城楼寄漳汀封连四州","aria-label":'Permalink to "登柳州城楼寄漳汀封连四州"'},"​")],-1)),e(r,{title:"登柳州城楼寄漳汀封连四州",author:"柳宗元",dynasty:"唐代",main:"城危荒景，思寄同贬",content:["城上高楼接大荒，海天愁思正茫茫。惊风乱飐芙蓉水，密雨斜侵薜荔墙。","岭树重遮千里目，江流曲似九回肠。共来百越文身地，犹自音书滞一乡。"],notes:["登上高楼，极目所见的是一派荒凉冷清的茫茫沙野，海天茫茫相接，与我的愁思一样四处弥漫。急风胡乱地掀动水中的荷花，密密的雨点斜打在长满薜荔的墙上。","层叠的远山连绵起伏，遮住了远眺的视线，那弯弯曲曲的柳江就如同我百结的愁肠。我们一起来到这边远的蛮荒之地，怎堪音书隔绝，人各一方。"]}),a[13]||(a[13]=t("h3",{id:"酬曹侍御过象县见寄",tabindex:"-1"},[n("酬曹侍御过象县见寄 "),t("a",{class:"header-anchor",href:"#酬曹侍御过象县见寄","aria-label":'Permalink to "酬曹侍御过象县见寄"'},"​")],-1)),e(r,{title:"酬曹侍御过象县见寄",author:"柳宗元",dynasty:"唐代",main:"骚人遥赠，碧山暮愁",content:["破额山前碧玉流，骚人遥驻木兰舟。","春风无限潇湘意，欲采蘋花不自由。"],notes:["破额山前，柳江如碧玉般流淌；您这位文人墨客，乘着小船在远方停泊并寄诗与我。","春风中满含着对潇湘故地的无限情思，我多想采摘苹花赠予您，却终究身不由己，难遂心愿。"]})])}const k=l(h,[["render",m],["__scopeId","data-v-bd77600c"]]);export{x as __pageData,k as default};
