import{_ as i,C as r,c as s,o as d,j as a,G as n,a as e}from"./chunks/framework.DEi4YP2n.js";const P=JSON.parse('{"title":"临江仙","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/诗词相关/词牌名/临江仙.md","filePath":"column/Poetry/诗词相关/词牌名/临江仙.md","lastUpdated":1759995509000}'),m={name:"column/Poetry/诗词相关/词牌名/临江仙.md"};function h(y,t,p,u,b,c){const l=r("ArticleMetadata"),o=r("PoetryDisplay");return d(),s("div",null,[t[0]||(t[0]=a("h1",{id:"临江仙",tabindex:"-1"},[e("临江仙 "),a("a",{class:"header-anchor",href:"#临江仙","aria-label":'Permalink to "临江仙"'},"​")],-1)),n(l),t[1]||(t[1]=a("blockquote",null,[a("p",null,[e("上片多写景，下片多写人；长调一泻，情绪却常收在“人生感喟”四字。"),a("br"),e(" 无论怀古、思乡还是赠别，骨子里都是——“"),a("code",null,"流年易逝，此身如寄"),e("”的苍凉与旷叹。")])],-1)),t[2]||(t[2]=a("h3",{id:"临江仙·滚滚长江东逝水",tabindex:"-1"},[e("临江仙·滚滚长江东逝水 "),a("a",{class:"header-anchor",href:"#临江仙·滚滚长江东逝水","aria-label":'Permalink to "临江仙·滚滚长江东逝水"'},"​")],-1)),n(o,{title:"临江仙·滚滚长江东逝水",author:"杨慎",dynasty:"明代",main:"长江逝水，笑尽英雄",content:["滚滚长江东逝水，浪花淘尽英雄。是非成败转头空。青山依旧在，几度夕阳红。","白发渔樵江渚上，惯看秋月春风。一壶浊酒喜相逢。古今多少事，都付笑谈中。"],notes:["滚滚长江向东流，多少英雄像翻飞的浪花般消逝。争什么是与非、成功与失败，到头来都是一场空。只有青山依然存在，太阳依然日升日落。","在江边的白发隐士，早已看惯了岁月的变化。和朋友难得见了面，痛快的畅饮一杯酒。古往今来的多少事，都付之于人们的谈笑中。"]}),t[3]||(t[3]=a("h3",{id:"临江仙·梦后楼台高锁",tabindex:"-1"},[e("临江仙·梦后楼台高锁 "),a("a",{class:"header-anchor",href:"#临江仙·梦后楼台高锁","aria-label":'Permalink to "临江仙·梦后楼台高锁"'},"​")],-1)),n(o,{title:"临江仙·梦后楼台高锁",author:"晏几道",dynasty:"宋代",main:"梦断楼空，相思成空",content:["梦后楼台高锁，酒醒帘幕低垂。去年春恨却来时。落花人独立，微雨燕双飞。","记得小蘋初见，两重心字罗衣。琵琶弦上说相思。当时明月在，曾照彩云归。(蘋 通：苹)"],notes:["深夜梦回楼台朱门紧锁，酒意消退但见帘幕重重低垂。去年的春恨涌上心头时，人在落花纷扬中幽幽独立，燕子在微风细雨中双双翱飞。","记得与小苹初次相见，身着两重心字香熏过的罗衣。琵琶轻弹委委诉说相思滋味。当时明月如今犹在，曾照着她彩云般的身影回归。"]}),t[4]||(t[4]=a("h3",{id:"临江仙·梅",tabindex:"-1"},[e("临江仙·梅 "),a("a",{class:"header-anchor",href:"#临江仙·梅","aria-label":'Permalink to "临江仙·梅"'},"​")],-1)),n(o,{title:"临江仙·梅",author:"李清照",dynasty:"宋代",main:"梅影冰魂，幽思自芳",content:["庭院深深深几许，云窗雾阁春迟。为谁憔悴损芳姿。夜来清梦好，应是发南枝。","玉瘦檀轻无限恨，南楼羌管休吹。浓香吹尽有谁知。暖风迟日也，别到杏花肥。"],notes:["庭院深深，不知有多深？云簇疏棂，雾迷高阁，春天总是迟迟不来。玉颜憔悴、芳姿瘦损,但是这番惦念、这番折磨又有谁知道呢？只有在夜晚的梦中才能相聚，向阳的梅枝也到了发芽的时节。","梅花风姿清瘦，南楼的羌笛不要吹奏哀怨的曲调。散发着浓浓的香味的梅花不知道被吹落多少？春日的暖风，别一下就让时间来到杏花盛开的时节了。"]}),t[5]||(t[5]=a("h3",{id:"临江仙·探梅",tabindex:"-1"},[e("临江仙·探梅 "),a("a",{class:"header-anchor",href:"#临江仙·探梅","aria-label":'Permalink to "临江仙·探梅"'},"​")],-1)),n(o,{title:"临江仙·探梅",author:"辛弃疾",dynasty:"宋代",main:"踏雪寻香，梅影寄心",content:["老去惜花心已懒，爱梅犹绕江村。一枝先破玉溪春。更无花态度，全有雪精神。","剩向空山餐秀色，为渠著句清新。竹根流水带溪云。醉中浑不记，归路月黄昏。"],notes:["老来惜花之心虽已减，爱梅还绕江村去探寻。玉溪梅花一枝先报春。毫无花儿柔媚娇艳的姿态，洁白雅淡全然是雪精神。","欣赏青山秀色已嫌多余，只为梅花写下清新词句。竹林下流水带走水上云。酒醉中全然记不得，回家路上月出天黄昏。"]}),t[6]||(t[6]=a("h3",{id:"临江仙·试问梅花何处好",tabindex:"-1"},[e("临江仙·试问梅花何处好 "),a("a",{class:"header-anchor",href:"#临江仙·试问梅花何处好","aria-label":'Permalink to "临江仙·试问梅花何处好"'},"​")],-1)),n(o,{title:"临江仙·试问梅花何处好",author:"张孝祥",dynasty:"宋代",main:"寻梅踏雪，清思成孤",content:["试问梅花何处好，与君藉草携壶。西园清夜片尘无。一天云破碎，两树玉扶疏。","谁擫(ye)昭华吹古调，散花便满衣裾。只疑幽梦在清都。星稀河影转，霜重月华孤。"],notes:["请问梅花好在哪里，那就让我们带着酒壶以草荐地而坐，细细品味吧。在这个静谧的夜晚，西园连仿佛连一丝尘埃都不存在，只见天空中云朵散开，而两棵梅花树则亭亭玉立，枝干舒展。","是谁轻抚着精美的玉管，吹奏起《梅花落》，悠扬的音乐仿佛让落花都随之飘洒，沾满了我们的衣襟。这一刻，我恍惚觉得自己置身于天上的清都仙境之中。夜空中星星稀疏，银河缓缓转动，寒霜凝重，月光孤高清冷。"]}),t[7]||(t[7]=a("h3",{id:"临江仙·柳外轻雷池上雨",tabindex:"-1"},[e("临江仙·柳外轻雷池上雨 "),a("a",{class:"header-anchor",href:"#临江仙·柳外轻雷池上雨","aria-label":'Permalink to "临江仙·柳外轻雷池上雨"'},"​")],-1)),n(o,{title:"临江仙·柳外轻雷池上雨",author:"欧阳修",dynasty:"宋代",main:"柳雷池雨，一霎生情",content:["柳外轻雷池上雨，雨声滴碎荷声。小楼西角断虹明。阑干倚处，待得月华生。","燕子飞来窥画栋，玉钩垂下帘旌。凉波不动簟纹平。水精双枕，傍有堕钗横。"],notes:["柳林外传来轻轻的雷鸣，池上细雨蒙蒙；雨声浙淅沥沥，滴在荷叶上发出细碎之声。不久小雨即停，小楼西角显现出被遮断的彩虹。我们靠倚栏杆旁，直等到月亮东升。","燕子飞回门前，窥伺着飞到画梁间；我从玉钩上放下门帘。床上竹席纹路平展，好像清凉的水波，却无波纹涌动。床头放着水晶双枕，她的金钗从发上坠下，横放枕边。"]}),t[8]||(t[8]=a("h3",{id:"临江仙·夜登小阁忆洛中旧游",tabindex:"-1"},[e("临江仙·夜登小阁忆洛中旧游 "),a("a",{class:"header-anchor",href:"#临江仙·夜登小阁忆洛中旧游","aria-label":'Permalink to "临江仙·夜登小阁忆洛中旧游"'},"​")],-1)),n(o,{title:"临江仙·夜登小阁忆洛中旧游",author:"陈与义",dynasty:"宋代",main:"小阁夜登，忆洛成空",content:["忆昔午桥桥上饮，坐中多是豪英。长沟流月去无声。杏花疏影里，吹笛到天明。","二十余年如一梦，此身虽在堪惊。闲登小阁看新晴。古今多少事，渔唱起三更。"],notes:["回忆当年在午桥畅饮，在座的都是英雄豪杰。月光映在河面，随水悄悄流逝，在杏花稀疏的花影中，吹起竹笛直到天明。","二十多年的经历好似一场梦，我虽身在，回首往昔却胆战心惊。闲来无事登上小阁楼观看新雨初晴的景致。古往今来多少历史事迹转瞬即逝，只有把它们编成歌的渔夫，还在那半夜里低声吟唱。"]})])}const x=i(m,[["render",h],["__scopeId","data-v-30001e16"]]);export{P as __pageData,x as default};
