import{_ as l,C as o,c as s,o as d,j as a,G as n,a as e}from"./chunks/framework.DEi4YP2n.js";const x=JSON.parse('{"title":"菩萨蛮","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/诗词相关/词牌名/菩萨蛮.md","filePath":"column/Poetry/诗词相关/词牌名/菩萨蛮.md","lastUpdated":1760582846000}'),m={name:"column/Poetry/诗词相关/词牌名/菩萨蛮.md"};function h(y,t,b,u,f,p){const i=o("ArticleMetadata"),r=o("PoetryDisplay");return d(),s("div",null,[t[0]||(t[0]=a("h1",{id:"菩萨蛮",tabindex:"-1"},[e("菩萨蛮 "),a("a",{class:"header-anchor",href:"#菩萨蛮","aria-label":'Permalink to "菩萨蛮"'},"​")],-1)),n(i),t[1]||(t[1]=a("blockquote",null,[a("p",null,[e("菩萨蛮这一词牌，无论写闺思、羁旅还是身世之感，骨子里只绕得开两个字——"),a("code",null,"怨与盼"),e("："),a("br"),e(" 幽怨眼前孤寂，盼归、盼春、盼长安； 用顿挫短句和频繁换韵，把“怨”压得低回，“盼”衬得愈发长远。")])],-1)),t[2]||(t[2]=a("h3",{id:"菩萨蛮·书江西造口壁",tabindex:"-1"},[e("菩萨蛮·书江西造口壁 "),a("a",{class:"header-anchor",href:"#菩萨蛮·书江西造口壁","aria-label":'Permalink to "菩萨蛮·书江西造口壁"'},"​")],-1)),n(r,{title:"菩萨蛮·书江西造口壁",author:"辛弃疾",dynasty:"宋代",main:"望断长安，郁愤山河",content:["郁孤台下清江水，中间多少行人泪。西北望长安，可怜无数山。","青山遮不住，毕竟东流去。江晚正愁余，山深闻鹧鸪。"],notes:["郁孤台下这赣江的流水，水中有多少苦难之人的眼泪。我举头眺望西北的长安，可惜只看到无数青山。","但青山怎能把江水挡住？浩浩江水终于还是向东流去。江边日晚我正满怀愁绪，听到深山传来声声鹧鸪悲鸣。"]}),t[3]||(t[3]=a("h3",{id:"菩萨蛮·人人尽说江南好",tabindex:"-1"},[e("菩萨蛮·人人尽说江南好 "),a("a",{class:"header-anchor",href:"#菩萨蛮·人人尽说江南好","aria-label":'Permalink to "菩萨蛮·人人尽说江南好"'},"​")],-1)),n(r,{title:"菩萨蛮·人人尽说江南好",author:"韦庄",dynasty:"唐代",main:"江南虽好，游子难归",content:["人人尽说江南好，游人只合江南老。春水碧于天，画船听雨眠。","垆边人似月，皓腕凝霜雪。未老莫还乡，还乡须断肠。"],notes:["人人都说江南好，来到这的游人本来就应该住在江南，直到老去。春天的江水清澈碧绿更胜天空的碧蓝，还可以在有彩绘的船听着雨声入眠。","江南酒家卖酒的女子美丽无比，盛酒撩袖时露出的双臂洁白如雪。年华未衰之时不要回乡，回到家乡后必定要愁肠寸断。"]}),t[4]||(t[4]=a("h3",{id:"菩萨蛮·大柏地",tabindex:"-1"},[e("菩萨蛮·大柏地 "),a("a",{class:"header-anchor",href:"#菩萨蛮·大柏地","aria-label":'Permalink to "菩萨蛮·大柏地"'},"​")],-1)),n(r,{title:"菩萨蛮·大柏地",author:"毛泽东",dynasty:"近现代",main:"战地重游，江山壮丽",content:["赤橙黄绿青蓝紫，谁持彩练当空舞？雨后复斜阳，关山阵阵苍。","当年鏖战急，弹洞前村壁。装点此关山，今朝更好看。"],notes:["天上挂着七色的彩虹，而又是谁手持着彩虹在空中翩翩起舞？黄昏雨之后又见夕阳，苍翠的群山仿如层层军阵。","当年这里曾经进行了一次激烈的苦战，子弹穿透了前面村子的墙壁。那前村墙壁上留下的累累弹痕，把这里装扮得更加美丽。"]}),t[5]||(t[5]=a("h3",{id:"菩萨蛮·回文夏闺怨",tabindex:"-1"},[e("菩萨蛮·回文夏闺怨 "),a("a",{class:"header-anchor",href:"#菩萨蛮·回文夏闺怨","aria-label":'Permalink to "菩萨蛮·回文夏闺怨"'},"​")],-1)),n(r,{title:"菩萨蛮·回文夏闺怨",author:"苏轼",dynasty:"宋代",main:"夏闺孤寂，回文寄怨",content:["柳庭风静人眠昼，昼眠人静风庭柳。香汗薄衫凉，凉衫薄汗香。","手红冰碗藕，藕碗冰红手。郎笑藕丝长，长丝藕笑郎。"],notes:["院无风，柳丝垂，闺人昼寝。闺人安静昼寝之际，起风，庭院柳条摇摆。微风吹，汗味透香气，薄衫生凉意。凉衫子散出清淡的汗香气。","红润的手端起了盛有冰块拌藕丝的小碗。盛有冰块拌藕丝的小碗冰冷了她红润的手。郎笑碗中的藕丝太长了。闺人一边吃长丝藕，一边又嘲笑她的情郎。"]}),t[6]||(t[6]=a("h3",{id:"菩萨蛮·回文秋闺怨",tabindex:"-1"},[e("菩萨蛮·回文秋闺怨 "),a("a",{class:"header-anchor",href:"#菩萨蛮·回文秋闺怨","aria-label":'Permalink to "菩萨蛮·回文秋闺怨"'},"​")],-1)),n(r,{title:"菩萨蛮·回文秋闺怨",author:"苏轼",dynasty:"宋代",main:"秋闺孤寂，回文寄怨",content:["井桐双照新妆冷，冷妆新照双桐井。羞对井花愁，愁花井对羞。","影孤怜夜永，永夜怜孤影。楼上不宜秋，秋宜不上楼。"],notes:["井栏边两棵梧桐树，望着我的冷色的新妆。我穿淡淡的新妆，初次望着两棵梧桐树立在井栏旁。我低垂地望着井栏边的花在凋落愁叹，发愁的花在井栏边望着我失色低头。","形影孤单，悲叹我度失眠难熬的长夜。长长的秋夜，可怜我这孤单的身影。楼上不适应这肃杀的秋色，萧瑟的秋色，我不应该上楼去眺望。"]}),t[7]||(t[7]=a("h3",{id:"菩萨蛮·回文冬闺怨",tabindex:"-1"},[e("菩萨蛮·回文冬闺怨 "),a("a",{class:"header-anchor",href:"#菩萨蛮·回文冬闺怨","aria-label":'Permalink to "菩萨蛮·回文冬闺怨"'},"​")],-1)),n(r,{title:"菩萨蛮·回文冬闺怨",author:"苏轼",dynasty:"宋代",main:"冬闺孤寂，回文寄怨",content:["雪花飞暖融香颊。颊香融暖飞花雪。欺雪任单衣。衣单任雪欺。","别时梅子结。结子梅时别。归不恨开迟。迟开恨不归。"],notes:["雪花飞暖融了少妇的香脸。香气的脸上融化了飞花似的雪。任凭雪花沾满单衣薄衫。单衣薄衫任凭雪花沾满。","离别时梅树初结青果。梅子初结青果时离别。只要他归来，我不怨恨梅花开得太晚。梅花迟开了，我愁恨的是他还不归。"]}),t[8]||(t[8]=a("h3",{id:"菩萨蛮·玉楼明月长相忆",tabindex:"-1"},[e("菩萨蛮·玉楼明月长相忆 "),a("a",{class:"header-anchor",href:"#菩萨蛮·玉楼明月长相忆","aria-label":'Permalink to "菩萨蛮·玉楼明月长相忆"'},"​")],-1)),n(r,{title:"菩萨蛮·玉楼明月长相忆",author:"温庭筠",dynasty:"唐代",main:"明月玉楼，长忆成痴",content:["玉楼明月长相忆，柳丝袅娜春无力。门外草萋萋，送君闻马嘶。","画罗金翡翠，香烛销成泪。花落子规啼，绿窗残梦迷。"],notes:["楼如白玉，楼外垂柳摇曳，正是暮春时节。梦中，萋萋的芳草、萧萧的马鸣，闺楼中的思妇，在明月之夜，正在苦苦地思忆着远方的离人。","罗帐上绣有一双金色的翡翠鸟，芳香的蜡烛融为滴滴的蜡泪。窗外残红飘落、子规啼血，窗内残梦凄迷、哀思绵绵。空楼相忆，思妇徒盼离人归来。"]}),t[9]||(t[9]=a("h3",{id:"菩萨蛮·满宫明月梨花白",tabindex:"-1"},[e("菩萨蛮·满宫明月梨花白 "),a("a",{class:"header-anchor",href:"#菩萨蛮·满宫明月梨花白","aria-label":'Permalink to "菩萨蛮·满宫明月梨花白"'},"​")],-1)),n(r,{title:"菩萨蛮·满宫明月梨花白",author:"温庭筠",dynasty:"唐代",main:"宫月梨雪，寂寂春思",content:["满宫明月梨花白，故人万里关山隔。金雁一双飞，泪痕沾绣衣。","小园芳草绿，家住越溪曲。杨柳色依依，燕归君不归。"],notes:["洒满庭中的月光啊，像院里的梨花一样白，你可照见我那思念的人儿么，相隔万里多少关塞。绣衣上一双金雁展翅欲飞，泪湿罗衫时更愁欢情难再。","看小园绿草凄凄，想起故乡弯弯的越溪。杨柳轻舞着依依春情，春燕归来带着无边的春意。燕归人却不归来，不知何时才能与他相聚。"]}),t[10]||(t[10]=a("h3",{id:"菩萨蛮·山亭水榭秋方半",tabindex:"-1"},[e("菩萨蛮·山亭水榭秋方半 "),a("a",{class:"header-anchor",href:"#菩萨蛮·山亭水榭秋方半","aria-label":'Permalink to "菩萨蛮·山亭水榭秋方半"'},"​")],-1)),n(r,{title:"菩萨蛮·山亭水榭秋方半",author:"朱淑真",dynasty:"宋代",main:"山亭秋半，孤影盼归",content:["山亭水榭秋方半，凤帏寂寞无人伴。愁闷一番新，双蛾只旧颦。","起来临绣户，时有疏萤度。多谢月相怜，今宵不忍圆。"],notes:["山亭水榭中秋色正浓，时节过半，凤帏里的女子仍是寂寞冷清，无人相伴。心中新添了一重愁闷，眉头依旧紧锁，没有丝毫舒展。","起身来到窗前在窗前，眼前不时有流萤飞过。多谢月亮对我有所怜惜，它今晚也不忍心变得圆满。"]}),t[11]||(t[11]=a("h3",{id:"菩萨蛮·湿云不动溪桥冷",tabindex:"-1"},[e("菩萨蛮·湿云不动溪桥冷 "),a("a",{class:"header-anchor",href:"#菩萨蛮·湿云不动溪桥冷","aria-label":'Permalink to "菩萨蛮·湿云不动溪桥冷"'},"​")],-1)),n(r,{title:"菩萨蛮·湿云不动溪桥冷",author:"苏轼",dynasty:"宋代",main:"湿云桥冷，怨锁春迟",content:["湿云不动溪桥冷。嫩寒初透东风影。桥下水声长。一枝和月香。","人怜花似旧。花比人应瘦。莫凭小栏干。夜深花正寒。"],notes:["潮湿的云彩凝滞不动，溪桥清冷，轻寒刚刚穿透东风的身影。桥下水发出长长的声响，河畔的梅枝正揉和着月光发出香气。","人爱花好似见了旧友，花与人比较起来该是俊秀清瘦。独自一人寂寞靠着那小小的栏杆，殊不知夜深正熬受着寒冷哩！"]}),t[12]||(t[12]=a("h3",{id:"菩萨蛮·雾窗寒对遥天暮",tabindex:"-1"},[e("菩萨蛮·雾窗寒对遥天暮 "),a("a",{class:"header-anchor",href:"#菩萨蛮·雾窗寒对遥天暮","aria-label":'Permalink to "菩萨蛮·雾窗寒对遥天暮"'},"​")],-1)),n(r,{title:"菩萨蛮·雾窗寒对遥天暮",author:"纳兰性德",dynasty:"清代",main:"雾窗暮寒，相思难寄",content:["雾窗寒对遥天暮，暮天遥对寒窗雾。花落正啼鸦，鸦啼正落花。","袖罗垂影瘦，瘦影垂罗袖。风翦一丝红，红丝一翦风。"],notes:["满是雾气的寒窗对着天空渐暝的暮色，傍晚的天空遥遥对着寒窗的雾花。花儿凋零，啼鸦声声。乌鸦开始啼叫，正值落花时节。","垂着罗袖的影子显得清瘦不已。清瘦的身影正垂着罗袖。和风剪出一丝丝红线，红线似春风剪得整整齐齐。"]})])}const k=l(m,[["render",h],["__scopeId","data-v-5aa09990"]]);export{x as __pageData,k as default};
