import{_ as i,C as r,c as s,o as d,j as a,G as n,a as e}from"./chunks/framework.0gGui_5R.js";const P=JSON.parse('{"title":"蝶恋花","description":"","frontmatter":{},"headers":[],"relativePath":"column/Poetry/诗词相关/词牌名/蝶恋花.md","filePath":"column/Poetry/诗词相关/词牌名/蝶恋花.md","lastUpdated":1760949915000}'),m={name:"column/Poetry/诗词相关/词牌名/蝶恋花.md"};function h(y,t,u,b,p,c){const l=r("ArticleMetadata"),o=r("PoetryDisplay");return d(),s("div",null,[t[0]||(t[0]=a("h1",{id:"蝶恋花",tabindex:"-1"},[e("蝶恋花 "),a("a",{class:"header-anchor",href:"#蝶恋花","aria-label":'Permalink to "蝶恋花"'},"​")],-1)),n(l),t[1]||(t[1]=a("blockquote",null,[a("p",null,[e("把人生最深的那点“"),a("code",null,"舍不得"),e("”写到极致——或惜春，或惜人，或自惜，总逃不过“"),a("code",null,"眼前就要失去，我却无能为力"),e("”的哀感。"),a("br"),e(" 词牌名里的“蝶”是扑花的，“花”是迟早凋的，一扑一凋之间，便是千古同慨的“舍不得”。")])],-1)),t[2]||(t[2]=a("h3",{id:"蝶恋花·阅尽天涯离别苦",tabindex:"-1"},[e("蝶恋花·阅尽天涯离别苦 "),a("a",{class:"header-anchor",href:"#蝶恋花·阅尽天涯离别苦","aria-label":'Permalink to "蝶恋花·阅尽天涯离别苦"'},"​")],-1)),n(o,{title:"蝶恋花·阅尽天涯离别苦",author:"王国维",dynasty:"近现代",main:"阅尽离苦，归梦成空",content:["阅尽天涯离别苦，不道归来，零落花如许。花底相看无一语，绿窗春与天俱莫。","待把相思灯下诉，一缕新欢，旧恨千千缕。最是人间留不住，朱颜辞镜花辞树。"],notes:["我早已历尽天涯离别的痛苦，想不到重回故地时，却看到百花如此零落的情景。忆当时与她花下别离，相对无言，只有离愁别绪噎胸间，现在只剩绿窗青天如故，却已非当时风景了。","想在这萤萤孤灯下细诉相思，细诉别后的相思。可是，一点点新的欢愉，又勾起了无穷的旧恨。在人世间最留不住的，是那在镜中一去不复返的青春和离树飘零的落花。"]}),t[3]||(t[3]=a("h3",{id:"蝶恋花·伫倚危楼风细细",tabindex:"-1"},[e("蝶恋花·伫倚危楼风细细 "),a("a",{class:"header-anchor",href:"#蝶恋花·伫倚危楼风细细","aria-label":'Permalink to "蝶恋花·伫倚危楼风细细"'},"​")],-1)),n(o,{title:"蝶恋花·伫倚危楼风细细",author:"柳永/欧阳修",dynasty:"宋代",main:"思到极致，甘之如饴",content:["伫(zhu)倚危楼风细细，望极春愁，黯黯生天际。","草色烟光残照里，无言谁会凭阑意。","拟把疏狂图一醉，对酒当歌，强乐还无味。","衣带渐宽终不悔，为伊消得人憔悴。"],notes:["我长时间倚靠在高楼的栏杆上，微风拂面一丝丝一细细，望不尽的春日离愁，沮丧忧愁从遥远无边的天际升起。","碧绿的草色，飘忽缭绕的云霭雾气弥漫在落日余晖里，默默无言谁理解我靠在栏杆上的心情。","本想尽情放纵喝个一醉方休，举杯高歌，勉强欢笑反而觉得毫无意味。","我日渐消瘦下去却始终不感到懊悔，宁愿为她消瘦得精神萎靡神色憔悴。"]}),t[4]||(t[4]=a("h3",{id:"蝶恋花·槛菊愁烟兰泣露",tabindex:"-1"},[e("蝶恋花·槛菊愁烟兰泣露 "),a("a",{class:"header-anchor",href:"#蝶恋花·槛菊愁烟兰泣露","aria-label":'Permalink to "蝶恋花·槛菊愁烟兰泣露"'},"​")],-1)),n(o,{title:"蝶恋花·槛菊愁烟兰泣露",author:"晏殊",dynasty:"宋代",main:"秋夜离恨，相思无尽",content:["槛菊愁烟兰泣露，罗幕轻寒，燕子双飞去。(双飞去：双来去)","明月不谙离恨苦，斜光到晓穿朱户。(离恨苦：离别苦)","昨夜西风凋碧树，独上高楼，望尽天涯路","欲寄彩笺兼尺素，山长水阔知何处？(兼：无)"],notes:["栏外的菊花笼罩着一层愁惨的烟雾，兰花沾露好似默默饮泣。罗幕闲垂，空气微寒，一双燕子飞去。","明月不明白离别之苦，斜斜的银辉直到破晓还穿入朱户。","昨天夜里秋风劲吹，凋零了绿树。我独自登上高楼，望尽那消失在天涯的道路。","想给我的心上人寄一封信。但是高山连绵，碧水无尽，又不知道我思念的人究竟在何处。"]}),t[5]||(t[5]=a("h3",{id:"蝶恋花·庭院深深深几许",tabindex:"-1"},[e("蝶恋花·庭院深深深几许 "),a("a",{class:"header-anchor",href:"#蝶恋花·庭院深深深几许","aria-label":'Permalink to "蝶恋花·庭院深深深几许"'},"​")],-1)),n(o,{title:"蝶恋花·庭院深深深几许",author:"冯延巳/欧阳修",dynasty:"宋代",main:"深院孤寂，春恨难遣",content:["庭院深深深几许，杨柳堆烟，帘幕无重数。玉勒雕鞍游冶处，楼高不见章台路。","雨横风狂三月暮，门掩黄昏，无计留春住。泪眼问花花不语，乱红飞过秋千去。"],notes:["庭院深深，不知有多深？杨柳依依，飞扬起片片烟雾，一重重帘幕不知有多少层。豪华的车马停在贵族公子寻欢作乐的地方，登上高楼也望不见通向章台的大路。","风狂雨骤的暮春三月，再是重门将黄昏景色掩闭，也无法留住春意。泪眼汪汪问落花可知道我的心意，落花默默不语，纷乱的，零零落落一点一点飞到秋千外。"]}),t[6]||(t[6]=a("h3",{id:"蝶恋花·出塞",tabindex:"-1"},[e("蝶恋花·出塞 "),a("a",{class:"header-anchor",href:"#蝶恋花·出塞","aria-label":'Permalink to "蝶恋花·出塞"'},"​")],-1)),n(o,{title:"蝶恋花·出塞",author:"纳兰性德",dynasty:"清代",main:"出塞怀古，山河苍凉",content:["今古河山无定据。画角声中，牧马频来去。满目荒凉谁可语？西风吹老丹枫树。","从前幽怨应无数。铁马金戈，青冢黄昏路。一往情深深几许？深山夕照深秋雨。"],notes:["从古至今江山兴亡都无定数，眼前仿佛战角吹响烽烟滚滚战马驰骋来来去去，黄沙遮日满目荒凉又能与谁说？秋风飒飒，只有那几株枫树，又一次被吹红了叶子。","从前愁苦凄凉的往事无穷无尽，金戈铁马之地，却是当年昭君舍身求和的路。曾经的一往情深有多深呢？犹如夕阳余辉照射下，深山之中的绵绵秋雨。"]}),t[7]||(t[7]=a("h3",{id:"蝶恋花·辛苦最怜天上月",tabindex:"-1"},[e("蝶恋花·辛苦最怜天上月 "),a("a",{class:"header-anchor",href:"#蝶恋花·辛苦最怜天上月","aria-label":'Permalink to "蝶恋花·辛苦最怜天上月"'},"​")],-1)),n(o,{title:"蝶恋花·辛苦最怜天上月",author:"纳兰性德",dynasty:"清代",main:"衔恨愿为天上月，年年犹得向郎圆",content:["辛苦最怜天上月，一昔如环，昔昔都成玦。若似月轮终皎洁，不辞冰雪为卿热。","无那尘缘容易绝，燕子依然，软踏帘钩说。唱罢秋坟愁未歇，春丛认取双栖蝶。"],notes:["最怜爱那天空辛苦的明月，一月之中只有一夜是像玉环一样的满月，其它时候都像是不完整的玉玦。如果能够像天上的圆月，长盈不亏，我将无惧月宫的寒冷，为你送去温暖。","无奈尘世的情缘最易断绝，而不懂忧愁的燕子依然轻轻地踏在帘钩上，呢喃絮语。我在秋日，面对你的坟茔，高歌一曲，然而愁绪丝毫没有削减。我是多么希望能和你像春天里双飞双宿的蝴蝶那样在草丛里嬉戏啊。"]}),t[8]||(t[8]=a("h3",{id:"蝶恋花·春暮",tabindex:"-1"},[e("蝶恋花·春暮 "),a("a",{class:"header-anchor",href:"#蝶恋花·春暮","aria-label":'Permalink to "蝶恋花·春暮"'},"​")],-1)),n(o,{title:"蝶恋花·春暮",author:"李冠/李煜",dynasty:"五代",main:"春晚人远，絮飞愁重",content:["遥夜亭皋闲信步。才过清明，渐觉伤春暮。数点雨声风约住。朦胧淡月云来去。","桃杏依稀香暗渡。谁在秋千，笑里轻轻语。一寸相思千万绪。人间没个安排处。"],notes:["夜间在亭台上闲适的踱着步子，清明刚过，便已经感觉到了春天渐渐逝去的气息。夜里飘落了几点雨滴后又停住，积云遮挡的月亮朦胧不明，云层也随着风移动。","桃花、杏花在暗夜中散发着幽幽香气，不知道在园内荡着秋千，轻声说笑的女子是谁？小小的心田里积聚着千丝万缕的相思意，辽阔的天地间竟没有一个地方可以安排这些愁绪。"]}),t[9]||(t[9]=a("h3",{id:"蝶恋花·黄菊开时伤聚散",tabindex:"-1"},[e("蝶恋花·黄菊开时伤聚散 "),a("a",{class:"header-anchor",href:"#蝶恋花·黄菊开时伤聚散","aria-label":'Permalink to "蝶恋花·黄菊开时伤聚散"'},"​")],-1)),n(o,{title:"蝶恋花·黄菊开时伤聚散",author:"晏几道",dynasty:"宋代",main:"菊开人散，醉泪书情",content:["黄菊开时伤聚散。曾记花前，共说深深愿。重见金英人未见。相思一夜天涯远。","罗带同心闲结遍。带易成双，人恨成双晚。欲写彩笺书别怨。泪痕早已先书满。"],notes:["每当黄菊盛开的时节，总会为离合聚散心生伤感。还记得从前在花前，我们曾一同诉说彼此深藏心底的心愿。如今再次见到这金黄的菊花，想见的人却迟迟未现。一夜相思煎熬，只觉天涯相隔愈发遥远。","用罗带不停地编织着同心结。带子要打成双结尚且容易，可人心啊，却总恨缘分来得太迟、相聚太难。想要在彩笺上写下离别后的怨愁，可尚未动笔，泪水早已先将纸页打湿沾满。"]})])}const x=i(m,[["render",h],["__scopeId","data-v-a57b81ae"]]);export{P as __pageData,x as default};
